/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText) {
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj) {
	obj.Visible = false;
}

function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		fn_selectAllGrid(obj,0,0,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}

function cb_sgbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(cb_sgbn.value == "" || cb_sgbn.value == "A") {
		cb_sayu.index = 0;
		cb_sayu.Enable = false;
	} else {
		cb_sayu.index = 0;
		cb_sayu.Enable = true;
	}
}

function fn_chkHno() {
	if(trim(ds_listH.Getcolumn(0, "S_SAYU")) > "") {
		cb_sayu.Enable = true;
	}

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "POSID") == posid && ds_list.GetColumn(i, "GUBUNA") == "Y") {
			ds_list.SetColumn(i, "CHK", true);
			return;
		}
	}
}

function fn_selectAllGrid(grdObj,nHeadCell,nBodyCell,bEvent) {
	var strColID = grdObj.GetCellProp("body", nBodyCell, "Colid");

	if(length(strColID) == 0) {
		return;
	}

	var dsObj = object(grdObj.BindDataset);

	grdObj.SetCellPos(nHeadCell);

	var nCount = dsObj.GetRowCount();

	var strChk = decode(grdObj.getCellProp("head", nHeadCell, "Text"), "1", "0", "1");

	grdObj.ReDraw = false;

	if(bEvent == false) {
		dsObj.FireEvent = false;
	}

	for(var i=0; i<dsObj.GetRowCount(); i++) {
		if(grdObj.GetCurEditType() == "checkbox" && dsObj.GetColumn(i, "GUBUNA") == "Y") {
			dsObj.SetColumn(i, strColID, strChk);
		}
	}

	if(bEvent == false) {
		dsObj.FireEvent = true;
	}

	grdObj.SetCellProp("head", nHeadCell, "Text", strChk);
	grdObj.ReDraw = true;
}
