function cb_MAKFDT_OnChanged(obj,strOldText,strNewText)
{
	var yymm = gfn_getBeforeMonth(substr(cb_MAKFDT.value,0,6),1);
    cb_MAKFDTT.VALUE =  yymm + "01";
}

