/********************************************************************************
* 기      능   : 이월 시 다음 월 입력
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
   if(strColumnID == "NEXT_MM")
    {
       for(var i=0; i<ds_list.GetRowCount(); i++)
       {
          if(ds_list.GetColumn(i,"NEXT_MM") == 'A')
          {  //alert( addMonth(ds_list.GetColumn(i,"MAKE_MM"), +1));
             ds_list.SetColumn(i,"NMON", addMonth(ds_list.GetColumn(i,"MAKE_MM"), +1));
          }
        }
    }
  // 컬럼 변경시 chk
    if(strColumnID!="CHK"){
		ds_list.SetColumn(nRow, "CHK", 1);
	}
}
/********************************************************************************
* 기      능   : 저장 (이월)
********************************************************************************/
function fn_save() 
{
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}


	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

//trace(ds_list2.SaveXML());
	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs04:BS0409002A_S5","bs04:BS0409002A_I1", );
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);

}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}
	 fn_query();
}
