/********************************************************************************
* 기      능   : 마감일저장 Callback  
********************************************************************************/
function fn_afterclosed(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");	
	}
}
