/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {	

	lv_mrrate1 = 0.0;
//	alert(PER_GBN);
	if(PER_GBN == "C")
	{
		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;

		tit_clearActionInfo();
		tit_addSearchActionInfo("bs04:BS0409002B_S1");             
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);

	}
    else if(PER_GBN == "N")
    {

		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;

		tit_clearActionInfo();
		tit_addSearchActionInfo("bs04:BS0409002B_S3");         		      
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);	

        ds_cond.SetColumn(0, "MAKE_MM", ds_master.GetColumn(0,"MAKE_MM"));
    }
}    

