/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{


	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트
	if(gfn_isNull(ed_bstnk, true, Static0.Text)) return false;
	//if(gfn_isNull(cb_waerk, true, Static32.Text)) return false;

	//if(cb_MAKTDT.text <= cb_closed.text)
	//{
	//	gfn_showAlert("CW00022","입력하신 활동월이 마감일 이전입니다.");
	//	return;
	//}

		  if(length(ds_master.GetColumn(0, "TYPE")) <=0 )
		{
			var arr = [parseInt(0) + 1, "MR유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

		  if(length(ds_master.GetColumn(0, "ACTION")) <=0 )
		{
			var arr = [parseInt(0) + 1, "ACTIVE유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 
	      if(length(ds_master.GetColumn(0, "CON_CH")) <=0 )
		{
			var arr = [parseInt(0) + 1, "계약변경"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 
	      if(ds_master.GetColumn(0, "NETWR") <= 0 )
		{
			var arr = [parseInt(0) + 1, "목표금액"];			
			gfn_showAlert("CW00224", arr);
			return;
		}    
		  if(length(ds_master.GetColumn(0, "BIGO1")) <=0 )
		{
			var arr = [parseInt(0) + 1, "활동내역"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

    ds_chk.clearData();
	if(PER_GBN == "N")
	{

		tit_clearActionInfo();
		tit_addSearchActionInfo("bs04:BS0409002B_S5"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}
	if(ds_chk.GetRowCount() > 0)	
	{
		gfn_showMsg("CW00137");
		return;
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs04:BS0409002B_S4", "bs04:BS0409002B_I1", "bs04:BS0409002B_U1");
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);

	ufn_enable("C");

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{ 	    
		gfn_showMsg("CI00003");		
	}
}
/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");		
		parent.fn_query();    
		fn_close();
	}
}
/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트
	if(gfn_isNull(ed_bstnk, true, Static0.Text)) return false;

	if(PER_GBN == "C")
	{
		if (!gfn_showConfirm("CI00015","MR계획보고서")) 
		{
			return;
		}	


		tit_clearActionInfo();	
		tit_addSingleActionInfo("bs04:BS0409002B_D2");
		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }

	//fn_reset("D");

}
