/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{

	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트
	if(gfn_isNull(ed_bstnk, true, Static0.Text)) return false;
	if(gfn_isNull(cb_waerk, true, Static32.Text)) return false;

	if(cb_MAKTDT.text <= cb_closed.text)
	{
		gfn_showAlert("CW00022","입력하신 활동월이 마감일 이전입니다.");
		return;
	}

		  if(length(ds_master.GetColumn(0, "TYPE")) <=0 )
		{
			var arr = [parseInt(0) + 1, "MR유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

		  if(length(ds_master.GetColumn(0, "ACTION")) <=0 )
		{
			var arr = [parseInt(0) + 1, "ACTIVE유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

	if(gfn_isNull(cb_MAKTDT, true, Static12.Text)) return false; 

	for(var i=0; i<ds_detail.GetRowCount(); i++)
	{
		// 필수 입력 항목	
		if(length(ds_detail.GetColumn(i, "ITEM")) <=0 )
		{
			var arr = [parseInt(i) + 1, "항목"];
			//gfn_showMsg("CW00224", arr);
			gfn_showAlert("CW00224", arr);
			return;
		} 

		/*
		if(ds_detail.GetColumn(i, "ACTDAT") > ds_detail.GetColumn(i, "ACTDAT2"))
		{
			var arr = [parseInt(i) + 1, "활동기간(TO)", "활동기간(FROM)"];
			//gfn_showMsg("CW00224", arr);
			gfn_showAlert("CW00037", arr);
			return;
		} 
		*/
	}


    ds_chk.clearData();
	if(PER_GBN == "N")
	{

		tit_clearActionInfo();

		tit_addSearchActionInfo("bs04:BS0409004B_S5"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}
//alert("2");
	if(ds_chk.GetRowCount() > 0)	
	{

		gfn_showMsg("CW00137");
		return;
	}


	tit_clearActionInfo();	


	tit_addSelectExActionInfo("bs04:BS0409004B_S4", "bs04:BS0409004B_I1", "bs04:BS0409004B_U1");

	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);

	ufn_enable("C");

    //parent.fn_query(); 


}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{

	    //detail 저장
		tit_clearActionInfo();	

		tit_addSingleActionInfo("bs04:BS0409004B_D1");
		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, //""
			, ""
			, ""
			,true  // ""
			);	


		if(ds_detail.GetRowCount() > 0)
		{

			tit_clearActionInfo();	

			tit_addMultiActionInfo("bs04:BS0409004B_I2");
			tit_callService(
				""
				, ""        
				, "ds_detail=ds_detail"
				, ""
				, ""
				, "fn_afterSave2"
				, ""
				, ""
				,true  // ""
				);	
		}

		//gfn_showMsg("CI00003");		

	}
}

