/********************************************************************************
* 기      능   : ds_detail 변경이벤트
********************************************************************************/
function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

//obj.fireevent = false;
	lv_mrrate1 = 0.0;


	if(strColumnID == "NETWR" or strColumnID == "WAVWR" )
	{
		ds_master.SetColumn(0,"MR_NETWR", ds_detail.Sum("NETWR") - ds_detail.Sum("WAVWR") );
		ds_master.SetColumn(0, "MR_NETWR_T", ds_detail.Sum("NETWR") - ds_detail.Sum("WAVWR") - ToFloat(MaskEdit9.Value) );
		var a,b;

	 a = ds_detail.Sum("NETWR");
	 b = ds_detail.Sum("WAVWR");
	 ds_master.SetColumn(0,"NETWR_G",a);
     ds_master.SetColumn(0,"WAVWR_S",b);

		if( ds_master.GetColumn(0,"NETWR_L") > 0 )
		{
			lv_mrrate1 = (( ds_master.GetColumn(0,"WAVWR_L") -  ds_master.GetColumn(0,"MR_NETWR") ) / 
							ds_master.GetColumn(0,"NETWR_L") ) * 100  ;		
            ds_master.SetColumn(0,"MR_RATE1", round(lv_mrrate1,1));
		}

        var lv_RETREN_AMT = ToFloat(ds_detail.GetColumn(nRow,"NETWR")) - ToFloat(ds_detail.GetColumn(nRow,"WAVWR"));



		ds_detail.SetColumn(nRow, "RETREN_AMT", lv_RETREN_AMT );
		if( ds_detail.GetColumn(nRow,"NETWR") > 0)
		{
			var lv_RETREN_RATE = round((ToFloat(ds_detail.GetColumn(nRow,"RETREN_AMT")) / ToFloat(ds_detail.GetColumn(nRow,"NETWR"))) * 100 ,1) ;	
			ds_detail.SetColumn(nRow, "RETREN_RATE",  lv_RETREN_RATE);
        }


		ds_master.SetColumn(0,"MR_RATE2", ds_master.GetColumn(0,"RATE_L") - ds_master.GetColumn(0,"MR_RATE1") );

		if(ds_master.GetColumn(0, "MR_RATE2") > 0)
		{
			st_img.Text = '△' ;
		}
		else if (ds_master.GetColumn(0, "MR_RATE2") < 0)
		{
			st_img.Text = '▽' ;
		}
		else
		{
			st_img.Text = '' ;
		}

    }		

//obj.fireevent = true;
}


/********************************************************************************
* 기      능   : cb_MAKTDT 변경이벤트
********************************************************************************/
function cb_MAKTDT_OnChanged(obj,strOldText,strNewText)
{
	ds_cond.SetColumn(0,"MAKE_MM",cb_MAKTDT.Value);
	for(var i=0; i<ds_detail.GetRowCount(); i++)
	{
		ds_detail.SetColumn(i, "MAKE_MM", cb_MAKTDT.Value);
	}
}



/********************************************************************************
* 기      능   : 마감월 관리
********************************************************************************/
function bt_closed_OnClick(obj)
{

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs04:BS0409001B_U2");     // 마감일 저장	
	tit_callService(
		""
		, ""
		, "ds_closedate=ds_closedate"
		, ""
		, ""
		, "fn_afterclosed"
		, ""
		, ""
		, true);


}


/********************************************************************************
* 기      능   : 마감일저장 Callback  
********************************************************************************/
function fn_afterclosed(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");	
	}
}




