/********************************************************************************
* 기      능   :  ds_zsdt0090  수주변경 리스트 가공
********************************************************************************/    
function ufn_zsdt0090()
{

	ds_zsdt0090.unFilter();
    for(var i=0; i<ds_zsdt0090.rowcount; i++)
	{
		ds_zsdt0090.SetColumn(i, "CHK", '');
		for(var j=0; j<ds_detail.rowcount; j++)
		{
			if(ds_detail.getcolumn(j,"ITEMNO") == ds_zsdt0090.getcolumn(i,"ITEMNO") )
			{				
				ds_zsdt0090.SetColumn(i, "CHK", 'X');
			}

		}
	}


	ds_zsdt0090.Filter("CHK!='X'");


}


/********************************************************************************
* 기      능   : ds_detail 변경이벤트
********************************************************************************/
function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//lv_mrrate1 = 0.0;
	var lv_index = 0;

	obj.fireevent = false;

	if(strColumnID == "ITEMNO")
	{

		lv_index = ds_zsdt0090.FindRow("ITEMNO",varNewValue);
		//ds_detail.SetColumn(nRow, "NETWR",      ds_zsdt0090.GetColumn(lv_index, "NETWR")    );				
		//ds_detail.SetColumn(nRow, "WAVWR",      ds_zsdt0090.GetColumn(lv_index, "WAVWR")    );	
		//ds_detail.SetColumn(nRow, "CHNETWR",    ds_zsdt0090.GetColumn(lv_index, "CHNETWR")  );		
		//ds_detail.SetColumn(nRow, "CHWAVWR",    ds_zsdt0090.GetColumn(lv_index, "CHWAVWR")  );

		ds_detail.SetColumn(nRow, "NETWR",      ds_zsdt0090.GetColumn(lv_index, "CHNETWR")    );				
		ds_detail.SetColumn(nRow, "WAVWR",      ds_zsdt0090.GetColumn(lv_index, "CHWAVWR")    );	
		ds_detail.SetColumn(nRow, "CHNETWR",    ds_zsdt0090.GetColumn(lv_index, "NETWR")  );		
		ds_detail.SetColumn(nRow, "CHWAVWR",    ds_zsdt0090.GetColumn(lv_index, "WAVWR")  );		





		ds_detail.SetColumn(nRow, "NETWR_GAP",  ds_zsdt0090.GetColumn(lv_index, "NETWR_GAP"));
		ds_detail.SetColumn(nRow, "WAVWR_GAP",  ds_zsdt0090.GetColumn(lv_index, "WAVWR_GAP"));

		ds_detail.SetColumn(nRow, "BUDGET_DAT",  ds_zsdt0090.GetColumn(lv_index, "BUDGET_DAT"));
		ds_detail.SetColumn(nRow, "ACTDAT2",  ds_zsdt0090.GetColumn(lv_index, "ACTDAT2"));


		ufn_zsdt0090();

		ufn_calculate();
        //trace(ds_detail.SaveXML());
     //   alert("aaa");

	}


	obj.fireevent = true;




}



/********************************************************************************
* 기      능   :  금액/율 계산
********************************************************************************/    
function ufn_calculate()   
{

    var lv_maxid = 0;
	var lv_minid = 0;

    ds_detail_tmp.ClearData();
	for(var i=0; i<ds_detail.rowcount; i++)
    {

		if(ds_detail.GetColumn(i,"CHK") != 'X' &&  ds_detail.GetColumn(i,"ITEMNO") >= 0)
		{
			ds_detail_tmp.addRow();
			ds_detail_tmp.copyrow(ds_detail_tmp.row,"ds_detail",i);
		}

    }

	lv_minid = ds_detail_tmp.FindRow("ITEMNO",ds_detail_tmp.Min("ITEMNO") );
//alert(ds_detail_tmp.Min("ITEMNO"));
//trace(ds_detail.SaveXML());
//trace(ds_detail_tmp.SaveXML());
	//ds_master.SetColumn(0, "NETWR_CH",   ds_detail_tmp.GetColumn(lv_minid, "NETWR")  );	
	//ds_master.SetColumn(0, "WAVWR_CH",   ds_detail_tmp.GetColumn(lv_minid, "WAVWR")  );	
//	alert(ds_detail_tmp.GetColumn(lv_minid, "CHNETWR"));
//	alert(ds_detail_tmp.GetColumn(lv_minid, "CHWAVWR"));


	ds_master.SetColumn(0, "NETWR_CH",   ds_detail_tmp.GetColumn(lv_minid, "CHNETWR")  );	
	ds_master.SetColumn(0, "WAVWR_CH",   ds_detail_tmp.GetColumn(lv_minid, "CHWAVWR")  );	



	if(ds_detail_tmp.rowcount > 0)
	{
		lv_maxid = ds_detail_tmp.FindRow("ITEMNO",ds_detail_tmp.Max("ITEMNO") );
		//ds_master.SetColumn(0, "NETWR_BF", (ds_master.GetColumn(0, "NETWR_L") - ds_detail_tmp.GetColumn(lv_maxid, "CHNETWR") ));	
		//ds_master.SetColumn(0, "WAVWR_BF", (ds_master.GetColumn(0, "WAVWR_L") - ds_detail_tmp.GetColumn(lv_maxid, "CHWAVWR") ));	
//alert( ds_master.GetColumn(0, "NETWR_L")  );
//alert( ds_detail_tmp.GetColumn(lv_maxid, "NETWR") );

		ds_master.SetColumn(0, "NETWR_BF", (ds_master.GetColumn(0, "NETWR_L") - ds_detail_tmp.GetColumn(lv_maxid, "NETWR") ));	
		ds_master.SetColumn(0, "WAVWR_BF", (ds_master.GetColumn(0, "WAVWR_L") - ds_detail_tmp.GetColumn(lv_maxid, "WAVWR") ));


	}
	else
	{
		ds_master.SetColumn(0, "NETWR_BF", 0.0);
		ds_master.SetColumn(0, "WAVWR_BF", 0.0);

	}



	//시행율계산
    if(ds_master.GetColumn(0,"NETWR_F") > 0)
    {
		ds_master.SetColumn(0,"RATE_F", round( (ds_master.GetColumn(0,"WAVWR_F") / ds_master.GetColumn(0,"NETWR_F")) * 100 ,1) );
    }
    if(ds_master.GetColumn(0,"NETWR_CH") > 0)
    {
		ds_master.SetColumn(0,"RATE_CH", round( (ds_master.GetColumn(0,"WAVWR_CH") / ds_master.GetColumn(0,"NETWR_CH")) * 100 ,1)  );
    }
    if(ds_master.GetColumn(0,"NETWR_L") > 0)
    {
		ds_master.SetColumn(0,"RATE_L", round( (ds_master.GetColumn(0,"WAVWR_L") / ds_master.GetColumn(0,"NETWR_L")) * 100 ,1)  );
    }

  //  ds_master.SetColumn(0, "MR_RATE2", round((( ds_master.GetColumn(0, "WAVWR_SUM") + ToFloat(MaskEdit9.Value) )/ ds_master.GetColumn(0, "NETWR_SUM"))* 100 ,1) );
   // ds_master.SetColumn(0, "RATE_S",ds_master.GetColumn(0,"RATE_F") - ds_master.GetColumn(0,"RATE_L"));	

   	 //  ds_master.SetColumn(0, "MR_RATE2", -(round((( ds_master.GetColumn(0, "WAVWR_SUM") + ToFloat(MaskEdit9.Value) )/ ds_master.GetColumn(0, "NETWR_SUM"))* 100 ,1) - ds_master.GetColumn(0, "RATE_CH")) );

       ds_master.SetColumn(0, "RATE_I",ds_master.GetColumn(0,"RATE_F") - ds_master.GetColumn(0,"RATE_L"));		



    ds_master.SetColumn(0, "NETWR_GAP", ds_master.GetColumn(0,"NETWR_L") - ds_master.GetColumn(0,"NETWR_F") );
    ds_master.SetColumn(0, "WAVWR_GAP", ds_master.GetColumn(0,"WAVWR_L") - ds_master.GetColumn(0,"WAVWR_F") );


	//ds_master.SetColumn(0, "MR_NETWR_T", ds_detail_tmp.Sum("NETWR_GAP") - ds_detail_tmp.Sum("WAVWR_GAP") );
//trace(ds_master.SaveXML());




    lv_mrrate1 = 0.0;
    if( ds_detail_tmp.GetColumn(lv_maxid, "NETWR") > 0 )
	{
		lv_mrrate1 = (ds_detail_tmp.GetColumn(lv_maxid, "WAVWR") / 
		              ds_detail_tmp.GetColumn(lv_maxid, "NETWR")   ) * 100  ;
	}  

    ds_master.SetColumn(0, "MR_RATE1", round(lv_mrrate1,1) );
  //  ds_master.SetColumn(0, "MR_RATE2", round((( ds_master.GetColumn(0, "WAVWR_SUM") + ToFloat(MaskEdit9.Value) )/ ds_master.GetColumn(0, "NETWR_SUM"))* 100 ,1) );

    ds_master.SetColumn(0, "MR_RATE2", ds_master.GetColumn(0, "RATE_CH") - ds_master.GetColumn(0, "MR_RATE1")  );


    ds_master.SetColumn(0, "NETWR_SUM",      ds_detail_tmp.Sum("NETWR") );
    ds_master.SetColumn(0, "WAVWR_SUM",      ds_detail_tmp.Sum("WAVWR") );
    ds_master.SetColumn(0, "NETWR_GAP_SUM",  ds_detail_tmp.Sum("NETWR_GAP") );

    ds_master.SetColumn(0, "WAVWR_GAP_SUM",  ds_detail_tmp.Sum("WAVWR_GAP") );

	var t_sum1, t_sum2;
     if(ToNumber(ds_master.GetColumn(0,"NETWR_GAP")) - ToNumber(ds_master.GetColumn(0,"WAVWR_GAP"))  < ToNumber(ds_master.GetColumn(0,"NETWR_GAP_SUM")) - ToNumber(ds_master.GetColumn(0,"WAVWR_GAP_SUM")) )
       {
//       alert("1");
		  t_sum1 = ToNumber(ds_master.GetColumn(0,"NETWR_GAP")) - ToNumber(ds_master.GetColumn(0,"WAVWR_GAP"));
//		  alert(ToNumber(ds_master.GetColumn(0,"NETWR_GAP")));
//		  alert(ToNumber(ds_master.GetColumn(0,"WAVWR_GAP")));
//		  alert(t_sum1);
          ds_master.SetColumn(0, "MR_NETWR_T", t_sum1);
//          ALERT("DDDD");
       }

       else {
//       alert("2");
           t_sum2 = ToNumber(ds_master.GetColumn(0,"NETWR_GAP_SUM")) - ToNumber(ds_master.GetColumn(0,"WAVWR_GAP_SUM"));
//           alert(ToNumber(ds_master.GetColumn(0,"NETWR_GAP_SUM")));
//		  alert(ToNumber(ds_master.GetColumn(0,"WAVWR_GAP_SUM")));
//		  alert(t_sum2);
           ds_master.SetColumn(0, "MR_NETWR_T",  t_sum2);
//           ALERT("AAA");
       }

/*
    var t_rate_i, t_rate2;
    t_rate_i = parseFloat(ds_master.GetColumn(0,"RATE_I"));
    t_rate2  = parseFloat(ds_master.GetColumn(0,"MR_RATE2")); // 

  alert(t_rate_i);
  alert(t_rate2);
     if(t_rate_i >  t_rate2)
	  { 
	      ds_master.SetColumn(0,"RATE_S",ds_master.GetColCount(0,"MR_RATE2"));
	        ALERT("aaaaa");
	   }  


	   else    
	  { 
	      ds_master.SetColumn(0,"RATE_S", ds_master.GetColumn(0,"RATE_I") );
	        ALERT("bbb");
	   }
*/ 
     ds_master.SetColumn(0, "MR_NETWR", ds_master.GetColumn( 0, "MR_NETWR_T"));

     if(ToFloat(MaskEdit9.Value) > 0  || ToFloat(MaskEdit23.Value) > 0 )  
      {

        var rate,rate2;

        rate = ToNumber(ds_master.GetColumn(0,"WAVWR_CH")) + ToNumber(ds_master.GetColumn(0,"WAVWR_GAP_SUM"));
        rate2 = ToNumber(ds_master.GetColumn(0,"NETWR_CH")) + ToNumber(ds_master.GetColumn(0,"NETWR_GAP_SUM"));

       ds_master.SetColumn(0,"MR_RATE1", round(((rate + ToFloat(MaskEdit9.Value) + ToFloat(MaskEdit23.Value)) / rate2)*100,1));
       ds_master.SetColumn(0,"MR_RATE2", ds_master.GetColumn(0,"RATE_CH") - ds_master.GetColumn(0,"MR_RATE1"));
        // ds_master.SetColumn(0, "MR_RATE2", (round((( ds_master.GetColumn(0, "WAVWR_SUM") - ToFloat(MaskEdit9.Value) - ToFloat(MaskEdit23.Value))/ ds_master.GetColumn(0, "NETWR_SUM"))* 100 ,1) - ds_master.GetColumn(0, "RATE_CH")) );


       }

      else {
              ds_master.SetColumn(0, "MR_RATE2", ds_master.GetColumn(0, "RATE_CH") - ds_master.GetColumn(0, "MR_RATE1")  );

       }




//  TRACE(ds_master.SaveXML());
// ALERT("XXX");








    if(ds_master.GetColumn(0, "MR_RATE2") > 0)
	{
		st_img.Text = '△' ;
	}
	else if (ds_master.GetColumn(0, "MR_RATE2") < 0)
	{
		st_img.Text = '▽' ;
	}
	else
	{
		st_img.Text = '' ;   

    }


}
