function btn_del_OnClick(obj)
{
	if(length(trim(ds_master.GetColumn(0,"BSTNK"))) <= 0)
	{
		gfn_showMsg("CW00054");
		return;
	}

//TRACE(ds_detail.SaveXML());
//ALERT(ds_detail.row);
	if(ds_detail.GetColumn(ds_detail.row, "CHK") == 'X')
	{
		gfn_showAlert("CW00022","저장분은 삭제하실수 없습니다.");
