/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{


	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트
	if(gfn_isNull(ed_bstnk, true, Static0.Text)) return false;
	if(gfn_isNull(cb_waerk, true, Static32.Text)) return false;

    if(length(ds_master.GetColumn(0, "DEPARTMENT")) <=0 )
		{
			var arr = [parseInt(0) + 1, "부서"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 
	  if(length(ds_master.GetColumn(0, "TYPE")) <=0 )
		{
			var arr = [parseInt(0) + 1, "MR유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

		  if(length(ds_master.GetColumn(0, "ACTION")) <=0 )
		{
			var arr = [parseInt(0) + 1, "ACTIVE유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 
			if(length(ds_detail.GetColumn(i, "ACTDAT")) <=0 )
		{
			var arr = [parseInt(i) + 1, "고객승인"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

		if(length(ds_detail.GetColumn(i, "CONTINUITY")) <=0 )
		{
			var arr = [parseInt(i) + 1, "시방유무"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 



	if(ds_detail.rowcount < 1)
	{
		gfn_showAlert("CW00022","활동내역이 한건도 없습니다.");
		return;
	}


	if(cb_MAKTDT.text <= cb_closed.text)
	{
		gfn_showAlert("CW00022","입력하신 활동월이 마감일 이전입니다.");
		return;
	}


	if(gfn_isNull(cb_MAKTDT, true, Static12.Text)) return false; 
//TRACE(ds_detail.SaveXML());

	for(var i=0; i<ds_detail.GetRowCount(); i++)
	{
		// 필수 입력 항목	
		if(ds_detail.GetColumn(i, "ITEMNO") < 0 )
		{
//ALERT(length(ds_detail.GetColumn(i, "ITEMNO")));		
//ALERT(ds_detail.GetColumn(i, "ITEMNO"));	
			var arr = [parseInt(i) + 1, "계약변경차수"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

		if(length(ds_detail.GetColumn(i, "ITEM")) <=0 )
		{
			var arr = [parseInt(i) + 1, "MR활동차수"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 


		if(length(ds_detail.GetColumn(i, "POSID")) <=0 )
		{
			var arr = [parseInt(i) + 1, "호기"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 



	}


    ds_chk.clearData();
	if(PER_GBN == "N")
	{

		tit_clearActionInfo();
		//tit_addSearchActionInfo("bs04:BS0409001B_S5"); 
		tit_addSearchActionInfo("bs04:BS0410002B_S5"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}

	if(ds_chk.GetRowCount() > 0)	
	{

		gfn_showMsg("CW00137");
		return;
	}


	tit_clearActionInfo();	
	//tit_addSelectExActionInfo("bs04:BS0409001B_S4", "bs04:BS0409001B_I1", "bs04:BS0409001B_U1");
	tit_addSelectExActionInfo("bs04:BS0410002B_S4", "bs04:BS0410002B_I1", "bs04:BS0410002B_U1");
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);

	ufn_enable("C");


    //detail 저장
    /*
    tit_clearActionInfo();	
	tit_addSingleActionInfo("bs04:BS0409001B_D1");
	tit_callService(
        ""
        , ""        
        , "ds_cond=ds_cond"
        , ""
        , ""
        , //""
		, ""
		, ""
        ,true  // ""
        );	


    if(ds_detail.GetRowCount() > 0)
    {
		tit_clearActionInfo();	
		//tit_addSingleActionInfo("bs04:BS0409001B_D1");
		tit_addMultiActionInfo("bs04:BS0409001B_I2");
		tit_callService(
			""
			, ""        
			, "ds_detail=ds_detail"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			,true  // ""
			);	
	}
	*/

}



