		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }



}



/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");		
		parent.fn_query();    
		fn_close();
	}
}


/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() 
{

	ds_print.ClearData();
    if(ds_detail.GetRowCount() > 0)
    {
		gfn_initDs(ds_print, true);	    

		ds_master.CopyToRow(0,"ds_print",0);


		for(var i=0; i<ds_detail.GetRowCount(); i++)
		{
			if(i>0)
			{
				ds_print.AddRow();			
			}
			ds_print.SetColumn(i,"ITEM_D", ds_detail.GetColumn(i,"ITEM"));			
			ds_print.SetColumn(i,"ITEMNO_D", ds_detail.GetColumn(i,"ITEMNO"));
			ds_print.SetColumn(i,"POSID_D", ds_detail.GetColumn(i,"POSID"));
			ds_print.SetColumn(i,"NETWR_D", ds_detail.GetColumn(i,"NETWR"));

			ds_print.SetColumn(i,"WAVWR_D", ds_detail.GetColumn(i,"WAVWR"));
			ds_print.SetColumn(i,"NETWR_GAP_D", ds_detail.GetColumn(i,"NETWR_GAP"));
			ds_print.SetColumn(i,"WAVWR_GAP_D", ds_detail.GetColumn(i,"WAVWR_GAP"));

			ds_print.SetColumn(i,"SAYU_D", ds_detail.GetColumn(i,"SAYU"));
			ds_print.SetColumn(i,"ACTDAT_D", ds_detail.GetColumn(i,"ACTDAT"));
			ds_print.SetColumn(i,"ACTDAT2_D", ds_detail.GetColumn(i,"ACTDAT2"));		

			ds_print.SetColumn(i,"BUDGET_DAT", ds_detail.GetColumn(i,"BUDGET_DAT"));
			ds_print.SetColumn(i,"BM_DAT", ds_detail.GetColumn(i,"BM_DAT"));	

		}


    }
    else
    {
		gfn_initDs(ds_print, true);	    
		ds_master.CopyToRow(0,"ds_print",0);
    }



	Dialog("BS04::BS0410001P.xml", "");
	//ds_print.ClearData();
//	TRACE(ds_print.SaveXML());
//ALERT("XXX");

}





