/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 

{
	ds_plan.ClearData();

	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(length(trim(ds_list.GetColumn(i, "MPLAN"))) > 0) {
			ds_plan.AddRow();

			ds_plan.SetColumn(j, "FLAG", "U");
			ds_plan.SetColumn(j, "VKBUR", ds_list.GetColumn(i,"VKBUR"));
			ds_plan.SetColumn(j, "PLAN_NETWR", ds_list.GetColumn(i,"MPLAN"));
			ds_plan.SetColumn(j, "MPLAN", ds_list.GetColumn(i,"MPLAN"));
			ds_plan.SetColumn(j, "MAKE_Y", substr(ds_cond.GetColumn(0,"MAKF_DT"),0,4));

			j++;
		}
	}

	tit_clearActionInfo();	

	tit_addSaveActionInfo(
        ""
        , "bs04:BS0410006B_I1"
        , ""
        , "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_plan=ds_plan"
        , ""
        , ""
        , "fn_afterSave");



}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
	}
}
