function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var gbn = "C";
	var pjt = ds_list.GetColumn(nRow, "PSPID");
	var mm =  ds_list.GetColumn(nRow, "MAKE_MM");
	var date = ds_list.GetColumn(nRow, "ERDATE");
	var tft = ds_list.GetColumn(nRow, "TFT");
	var ernam = ds_list.GetColumn(nRow, "ERNAMNM");
	//var hno = ds_list2.GetColumn(nRow, "POSID_1");

 //alert(tft);
 //aler(ernam);

	var arg = "PER_GBN="+quote(gbn);
	    arg += " PRE_PJT="+quote(pjt);
		arg += " PRE_MM="+quote(mm);
		arg += " PRE_DATE="+quote(date);


	//SELFLG = "N";
//alert(arg);
	if(G_USER_ID == tft || G_USER_ID == ernam ||  G_USER_ID == "2022702" || G_USER_ID == "2018578"|| G_USER_ID == "2024586" || G_USER_ID == '2029847') {
		gfn_openPopup("BS0410007B", true, arg);
		}

    else{

          global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
	      global.FRM_BOTTOM.st_msg.color = "yellow";       
	      global.FRM_BOTTOM.st_msg.value = "작성자와 TFT리더만 조회 가능합니다.";

    }


//	gfn_openPopup("BS0410007B", true, arg);

}


