function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 

/*	gfn_initForm(true);

	ed_pjt.SetFocus();

	gfn_showMsg("CI00007");

	PRE_MM = PRE_MM + "01";
	cb_MAKTDT.text = PRE_MM ;
	ds_head.SetColumn(0,"MAKE_MM",PRE_MM);

*/
	gfn_initForm();	



	//gfn_initDs(ds_master, true);	
	//gfn_initDs(ds_cond, true);	
	//gfn_initDs(ds_detail, true);	

	PER_GBN = PER_GBN;
	PRE_MM = PRE_MM + "01";
	PRE_DATE = PRE_DATE;
	PRE_PJT = PRE_PJT;

    // 소수점 최소 첫째자리까지 나오게...
    MaskEdit3.DecimalMinLen =1;
    MaskEdit0.DecimalMinLen = 1;
    MaskEdit12.DecimalMinLen = 1;
    MaskEdit5.DecimalMinLen = 1;

	ds_authority.ClearData();
	ds_closedate.ClearData();

	tit_clearActionInfo();	
	tit_addSearchActionInfo("bs04:BS0409001B_S7");     // 권한    
	tit_addSearchActionInfo("bs04:BS0409001B_S9");     // 마감일
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_authority=ds_authority  ds_closedate=ds_closedate"
		, ""
		, ""//"fn_afterQuery"
		, ""
		, ""
		, true);



//마감버튼
	if(ds_authority.GetRowCount() > 0)
	{
		bt_closed.Visible = true;
		cb_closed.Enable = true;
	}
	else
	{
		bt_closed.Visible = false;
		cb_closed.Enable = false;
	}


	//마감월
	//cb_closed.Text = ds_closedate.GetColumn(0,"CLOSEDATE") + '01';
   //alert(PRE_DATE);

	if(PER_GBN == "C")
	{
		ed_pjt.text = PRE_PJT;	
		//var lv_mm = PRE_MM + '01';
		cb_MAKTDT.text = PRE_MM  ;
		ds_cond.SetColumn(0,"MAKE_MM",PRE_MM);

		div_btn.btn_query.visible = false;

		ed_erdat.Text = substr(PRE_DATE,0,4) + "." + 
		                substr(PRE_DATE,4,2) + "." + 
		                substr(PRE_DATE,6,2);



		ufn_enable("C");
		fn_query();
		//alert(ed_erdat.Text);

	}

	else if(PER_GBN == "N")
	{
		div_btn.btn_query.visible = true;
		ed_erdat.Text = substr(gfn_getCurrDate(),0,4) + "." + 
		                substr(gfn_getCurrDate(),4,2) + "." + 
		                substr(gfn_getCurrDate(),6,2);

		ufn_enable("N");

	//	alert(ed_erdat.Text);

	}


}

function ufn_enable(gbn)
{
	if(gbn=="C")
	{
		ed_pjt.Enable = false;
		btn_pjt.Enable = false;

		cb_MAKTDT.Enable = true;
		//cd_action.Enable = false;
		div_btn.btn_query.visible = false;


	}

	else if(gbn=="N")
	{   	    
	    cb_MAKTDT.Enable = true;
	    //cd_action.Enable = true;
	    div_btn.btn_query.visible = true;
	}


}
