function fn_afterQuery2(errCode, errMsg) {
	//ds_cond_copy.copy(ds_cond);

    ds_list.SetColumn(ds_list.currow,"PLANSUM", 0);
	ds_list.SetColumn(ds_list.currow,"LASTSUM", 0);
	ds_list.SetColumn(ds_list.currow,"NETWR_MM", 0);
	ds_list.SetColumn(ds_list.currow,"TOTAL", 0);
    ds_list.SetColumn(ds_list.currow,"GAP",0);
    ds_list.SetColumn(ds_list.currow,"NETWR_T", 0);


   if(PER_GBN == "C")	// 조회
	{


		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;
		if(gfn_isNull(cb_MAKTDT, true, Static12.Text)) return;		

		tit_clearActionInfo();
		//tit_addSearchActionInfo("bs04:BS0409001B_S1");         
	    if(ds_cnt.GetColumn(0,"CNT") > 0)

		tit_addSearchActionInfo("bs04:BS0410007B_S5");    // ZSDTW012    해당 월의 데이터

	    else
		tit_addSearchActionInfo("bs04:BS0410007B_S4");   // VBAK 비어있는 데이터.

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery3"
			, ""
			, ""
			, true);

	}
    else if(PER_GBN == "N")	// 신규 입력
    {


		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;


		tit_clearActionInfo();
		//tit_addSearchActionInfo("bs04:BS0409001B_S3");         		      
		 if(ds_cnt.GetColumn(0,"CNT") > 0)

		tit_addSearchActionInfo("bs04:BS0410007B_S12");    // ZSDTW012     MAX(MAKE_MM)

	    else
		tit_addSearchActionInfo("bs04:BS0410007B_S4");   
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery3"
			, ""
			, ""
			, true);	


        ds_cond.SetColumn(0, "MAKE_MM", ds_head.GetColumn(0,"MAKE_MM"));

		for(var i=0; i<ds_list.GetRowCount(); i++)
		{
			ds_list.SetColumn(i, "MAKE_MM", cb_MAKTDT.Value);
		}


		//ufn_calculate();


    }


}
