/********************************************************************************
* 기      능   :  금액/율 계산
********************************************************************************/    
function ufn_calculate()   
{
    var rate;
    var rate2;
    var rate3;

// 시행률 구하기
    if(ToFloat(MaskEdit1.Value) > 0)  
     {
         rate = ToNumber(ds_head.GetColumn(0,"RATE")) - ToNumber(ds_head.GetColumn(0,"RATE_T"));
     }
         ds_head.SetColumn(0,"RATE_P",rate);


// Target 금액대비 달성율  (진척률구하는거!)
    // if(ds_list.rowcount >=  0)  
      if(ToFloat(MaskEdit4.Value) > 0)  
     {
         //rate3 = ds_list.Sum("NETWR_MM");
         rate2 = (ds_list.Sum("TOTAL") / ToFloat(MaskEdit4.Value)) * 100 ;
     }
        MaskEdit12.value = round(rate2,1);



//추진금액, 누계,실적,TOTAL 합

    ds_head.SetColumn(0, "NETWR_MM_SUM", ds_list.Sum("NETWR_MM") );
    ds_head.SetColumn(0, "TOTAL_SUM",    ds_list.Sum("TOTAL") );
    ds_head.SetColumn(0, "PLANSUM_SUM",  ds_list.Sum("PLANSUM_C") );
    ds_head.SetColumn(0, "LASTSUM_SUM",  ds_list.Sum("LASTSUM") );

// 시행률 (+,-)
    if(ds_head.GetColumn(0, "RATE_P") > 0)
	{
		st_img.Text = '△';
	}
	else if (ds_head.GetColumn(0, "RATE_P") < 0)
	{
		st_img.Text = '▽';
	}
	else
	{
		st_img.Text = '';   
    }



}

