/********************************************************************************
* 기      능   : (+)버튼 클릭시
********************************************************************************/
function btn_add_OnClick(obj)
{

	if(length(trim(ds_head.GetColumn(0,"ZSITE_NM"))) <= 0)
	{

		gfn_showMsg("CW00054");
		return;
	}	


	ds_list.AddRow();			

	ds_list.SetColumn(ds_list.currow,"PSPID", ds_cond.GetColumn(0,"PSPID"));
	ds_list.SetColumn(ds_list.currow,"MAKE_MM", ds_head.GetColumn(0,"MAKE_MM"));
	ds_list.SetColumn(ds_list.currow,"FINISH", "");
	ds_list.SetColumn(ds_list.currow,"WAERK", ds_head.GetColumn(0,"WAERK"));

	ds_list.SetColumn(ds_list.currow,"PLANSUM", 0);
	ds_list.SetColumn(ds_list.currow,"LASTSUM", 0);
	ds_list.SetColumn(ds_list.currow,"NETWR_MM", 0);
	ds_list.SetColumn(ds_list.currow,"TOTAL", 0);
    ds_list.SetColumn(ds_list.currow,"GAP", 0);
    ds_list.SetColumn(ds_list.currow,"NETWR_T", 0);


     ds_list.fireevent = true;

     ufn_calculate();



}


/********************************************************************************
* 기      능   : (-)버튼 클릭시
********************************************************************************/
function btn_del_OnClick(obj)
{
	if(length(trim(ds_head.GetColumn(0,"ZSITE_NM"))) <= 0)
	{
		gfn_showMsg("CW00054");
		return;
	}

	if(ds_list.GetColumn(ds_list.row, "FINISH") == 'X')
	{
		gfn_showAlert("CW00022","저장분은 삭제하실수 없습니다.");
		return;
	}



	ds_list.DeleteRow(ds_list.row);

//	ufn_zsdt0090();
	ufn_calculate();
}
