/********************************************************************************
* 기      능   :  시행률 계산
********************************************************************************/    

function MaskEdit1_OnCharChanged(obj,strPreText,strPostText)
{
 //시행률계산
    lv_mrrate1 = 0.0;

    if( ToFloat(MaskEdit1.Value) > 0 )
	{
		lv_mrrate1 = ( ToFloat(MaskEdit2.Value) / ToFloat(MaskEdit1.Value) ) * 100  ;
	}  
      // alert(lv_mrrate1);

     ds_head.SetColumn(0, "RATE", round(lv_mrrate1,1) );

 //Target MR시행률계산
     lv_mrrate2 = 0.0;
     lv_mrrate3 =0.0;

    if( ToFloat(MaskEdit4.Value) > 0 )
    {
       lv_mrrate2 = ((ToFloat(MaskEdit2.Value) - ToFloat(MaskEdit4.Value) ) / ToFloat(MaskEdit1.Value)) * 100;        
    }

    //ds_head.SetColumn(0, "RATE_T", round(lv_mrrate2,1) );
      ds_head.SetColumn(0, "RATE_T", round(lv_mrrate2,1) );
    ufn_calculate();


}

function MaskEdit2_OnCharChanged(obj,strPreText,strPostText)
{

 //시행률계산
  lv_mrrate1 = 0.0;
  if( ToFloat(MaskEdit2.Value) > 0 )
	{
		lv_mrrate1 = ( ToFloat(MaskEdit2.Value) / ToFloat(MaskEdit1.Value) ) * 100  ;
	}  

     ds_head.SetColumn(0, "RATE", round(lv_mrrate1,1) );

//Target MR시행률계산
   lv_mrrate2 = 0.0;

    if( ToFloat(MaskEdit4.Value) > 0 )
    {
       lv_mrrate2 = ((ToFloat(MaskEdit2.Value) - ToFloat(MaskEdit4.Value) ) / ToFloat(MaskEdit1.Value)) * 100 ;
    }

    ds_head.SetColumn(0, "RATE_T", round(lv_mrrate2,1) );   

  //  ds_head.SetColumn(0,"RATE_P", lv_mrrate1 - lv_mrrate2);
  ufn_calculate();

}

function MaskEdit4_OnCharChanged(obj,strPreText,strPostText)
{

//Target MR시행률계산
  lv_mrrate1 = 0.0;
//  lv_mrrate2 = 0.0;

  if( ToFloat(MaskEdit4.Value) > 0 )
	{
		lv_mrrate1 = ((ToFloat(MaskEdit2.Value) - ToFloat(MaskEdit4.Value) ) / ToFloat(MaskEdit1.Value)) * 100 ;
	    ds_head.SetColumn(0, "RATE_T", round(lv_mrrate1,1) ) ;
	    ufn_calculate();

	} 

}


/********************************************************************************
* 기      능   : cb_MAKTDT 변경이벤트
********************************************************************************/
function cb_MAKTDT_OnChanged(obj,strOldText,strNewText)
{
	ds_head.SetColumn(0,"MAKE_MM",cb_MAKTDT.Value);

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		ds_list.SetColumn(i, "MAKE_MM", cb_MAKTDT.Value);
	}
}





function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
    // ds_list.Copy(ds_list.GetColumn(0,"PLANSUM"))
 for(var i=0; i<ds_list.GetRowCount(); i++)
{
    if(ds_list.GetColumn(i,"ACTIVE_R") == 'D')
     {
         ds_list.SetColumn(i,"PLANSUM_C",0);
      }

     else
     { 
         ds_list.SetColumn(i,"PLANSUM_C",ds_list.GetColumn(i,"PLANSUM"));
     }
}

   var lv_total = ToFloat(ds_list.GetColumn(nRow,"NETWR_MM")) + ToFloat(ds_list.GetColumn(nRow,"LASTSUM"));



	ds_list.SetColumn(nRow, "TOTAL", lv_total );


 var lv_gap = ToFloat(ds_list.GetColumn(nRow,"NETWR_T")) - ToFloat(ds_list.GetColumn(nRow,"TOTAL"));


	ds_list.SetColumn(nRow, "GAP", lv_gap );

    // ALERT(ds_list.GetColumn(i,"TOTAL"));
//	alert("aaa");
	ufn_calculate();
}

