		, true);


}


/********************************************************************************
* 기      능   : 마감일저장 Callback  
********************************************************************************/
function fn_afterclosed(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");	
	}
}


/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트

	if(PER_GBN == "C")
	{
		if (!gfn_showConfirm("CI00015","MR수행록")) 
		{
			return;
		}	


		tit_clearActionInfo();	

		tit_addMultiActionInfo("bs04:BS0410007B_D3");	
		tit_addMultiActionInfo("bs04:BS0410007B_D1");						
		tit_addMultiActionInfo("bs04:BS0410007B_D2");	

		tit_callService(
			""
			, ""        
			, "ds_head=ds_head ds_list2=ds_list2"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }

	//fn_reset("D");

}  

function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");		
		parent.fn_query();    
		fn_close();
	}
}

/********************************************************************************
