********************************************************************************/
function fn_print() 
{

	ds_print.ClearData();
    if(ds_list.GetRowCount() > 0)
    {
		gfn_initDs(ds_print, true);	    

		ds_head.CopyToRow(0,"ds_print",0);



		for(var i=0; i<ds_list.GetRowCount(); i++)
		{
			if(i>0)
			{
				ds_print.AddRow();			
			}
		        ds_print.SetColumn(i,"ITEMNO2",ds_list.GetColumn(i,"ITEMNO"));
                //ds_print.SetColumn(i,"CONTR_F2",ds_list.GetColumn(i,"CONTR_F"));
                ds_print.SetColumn(i,"PLANSUM",ds_list.GetColumn(i,"PLANSUM"));
