/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	// 필수 입력사항 및 일자 체크 
	if(!gfn_checkFromToObj(cal_fdt, cal_tdt, true, st_date.value)) {
		return;
	}

	if(length(med_mno.Value) > 0 && length(med_mno.Value) < 13) {
		gfn_showMsg("문서번호를 정확히 입력하십시요.");
		return;
	}

	ds_cond.SetColumn(0, "MRT", substr(med_mno.Value,0,8));
	ds_cond.SetColumn(0, "MSQ", substr(med_mno.Value,8,5));
	ds_cond.SetColumn(0, "HIS", chk_his.Value);

	if(cb_pst.Value == "25" || cb_pst.Value == "35") {
		ds_cond.SetColumn(0, "PGB", "A");
	} else {
		ds_cond.SetColumn(0, "PGB", "B");
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	grd_list.SetCellProp("head", 1, "Text", 0);

	var sql = "";

	if(chk_his.Value == false) { //최종차수만 조회
		sql = "bs05:BS0501001A_S6";
	} else { //이력조회
		sql = "bs05:BS0501001A_S2";
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(sql);
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}

function fn_afterSearchVkgrp(errCode, errMsg) {
	gfn_insertEmptyRow(ds_vkgrp, G_CODE_ALL);
	cb_vkgrp.Index = 0;
}

function fn_afterSearchLoginUser(errCode, errMsg) {
	var user_grp = trim(G_USER_GROUP);

	if(length(user_grp) == 0) {
		user_grp = trim(G_USER_GROUP_B);
	}
/*
	ufn_enableBtn("approval", false);
	ufn_enableBtn("delete", false);
	ufn_enableBtn("print", false);
	ufn_enableBtn("trans", false);
	ufn_enableBtn("sendback", false);
*/
	user_grp = right(user_grp,2);

	if(trim(ds_loginuser.GetColumn(0, "DESC")) <> "부서장") {
		if(user_grp == "20") {
			cb_vkbur.Value = ds_loginuser.GetColumn(0, "VKBUR");
			cb_vkgrp.Value = ds_loginuser.GetColumn(0, "VKGRP");
		} else if(user_grp == "30") {
			cb_vkbur.Value = ds_loginuser.GetColumn(0, "VKBUR");
			cb_vkgrp.Value = ds_loginuser.GetColumn(0, "VKGRP");
		} else if(user_grp == "40") {
		}
	}

	fn_searchUserCode();
}

function fn_afterSearchUser(errCode, errMsg) {
//	gfn_insertEmptyRow(ds_user, G_CODE_ALL);
//	cb_user.Index = 0;

	var user_grp = trim(G_USER_GROUP);

	if(length(user_grp) == 0) {
		user_grp = trim(G_USER_GROUP_B);
	}

	user_grp = right(user_grp,2);

	if(trim(ds_loginuser.GetColumn(0, "DESC")) <> "부서장") {
		if(user_grp == "20") {
			ds_user.Filter("CODE == " + quote(ds_loginuser.GetColumn(0, "NUMB")));
			cb_user.Index = 0;
			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_user.Enable = false;

			ufn_enableBtn("approval", false);
			ufn_enableBtn("delete", true);
			ufn_enableBtn("trans", true);
			ufn_enableBtn("sendback", false);
		} else if(user_grp == "30") {
			ds_user.Filter("TEAM == " + quote(ds_loginuser.GetColumn(0, "VKGRP")));
			gfn_insertEmptyRow(ds_user, G_CODE_ALL);
			cb_user.Index = 0;
			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_user.Enable = true;

			ufn_enableBtn("approval", true);
			ufn_enableBtn("delete", true);
			ufn_enableBtn("trans", true);
			ufn_enableBtn("sendback", true);
		} else if(user_grp == "40") {
			gfn_insertEmptyRow(ds_user, G_CODE_ALL);
			cb_user.Index = 0;
			cb_vkbur.Enable = true;
			cb_vkgrp.Enable = true;
			cb_user.Enable = false;

			ufn_enableBtn("approval", true);
			ufn_enableBtn("delete", true);
			ufn_enableBtn("trans", true);
			ufn_enableBtn("sendback", true);
		}
	} else {
		cb_vkbur.Value = ds_loginuser.GetColumn(0, "VKBUR");
		ds_vkgrp.Filter("SUBSTR(CODE,0,2) == " + quote(substr(ds_loginuser.GetColumn(0, "VKBUR"),0,2)));
		gfn_insertEmptyRow(ds_vkgrp, G_CODE_ALL);
		cb_vkgrp.Index = 0;
		gfn_insertEmptyRow(ds_user, G_CODE_ALL);
		cb_user.Index = 0;
		cb_vkbur.Enable = false;
		cb_vkgrp.Enable = true;
		cb_user.Enable = false;

		ufn_enableBtn("approval", true);
		ufn_enableBtn("delete", true);
		ufn_enableBtn("trans", true);
		ufn_enableBtn("sendback", true);
	}
}

function fn_afterVkgrpChange(errCode, errMsg) {
	gfn_insertEmptyRow(ds_vkgrp, G_CODE_ALL);
	cb_vkgrp.Index = 0;
}

