		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 삭제할 정보 없음
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	if(fn_chkList("A") == false) {
		return false;
	} else {
		tit_clearActionInfo();

		tit_addSaveActionInfo(
			""
			, "bs05:BS0501001A_U4"
			, ""
			, "FLAG"
			, ""
			, -1
			, "");

		tit_callService(
			""
			, ""
			, "ds_list=ds_list:A"
			, ""
			, ""
			, "fn_afterDelete");
	}
}

