/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {
	gfn_clearMsgBar();
	ds_list1.ClearData();
	ds_list2.ClearData();

	if(ds_list.rowCount() < 1) {
		// 출력할 정보 없음
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	var k = 0;
	var z = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			z = i;
			k++;
		}
	}

	if(k == 0 || k > 1) {
		gfn_showMsg("견적서 출력은 한건씩 가능합니다.");
		return false;
	}

	ds_cond1.SetColumn(0,"DEP",ds_list.GetColumn(z,"DEP"));
	ds_cond1.SetColumn(0,"RDT",ds_list.GetColumn(z,"RDT"));
	ds_cond1.SetColumn(0,"SEQ",ds_list.GetColumn(z,"SEQ"));
	ds_cond1.SetColumn(0,"SIR",ds_list.GetColumn(z,"SIR"));

	if(fn_chkList("E") == false) {
		return false;
	} else {
		tit_clearActionInfo();
		tit_addSearchActionInfo("bs05:BS0501001B_S1");
		tit_addSearchActionInfo("bs05:BS0501001B_S2");
		tit_callService(
			""
			, ""
			, "ds_cond1=ds_cond1"
			, "ds_list1=ds_list1 ds_list2=ds_list2"
			, ""
			, "fn_afterPrint"
			, true
			, true);
	}
}

