function fn_chkList(knd) {
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "DEL") == "N") { //진행상태가 삭제가 아닌 경우
				// 10 : 작성, 15 : 대리점반송, 20 : 견적반송, 25 : 전송, 30 : 접수, 35 : 확정, 40 : 승인
				if(knd == "A") { //삭제
					if(ds_list.GetColumn(i, "PST") > "20") {
						var arr = [parseInt(i) + 1];
						gfn_showMsg("CW00042", arr);
						return false;
					}
				}
				if(knd == "B") { //전송
					if(ds_list.GetColumn(i, "PST") > "10") {
						var arr = [parseInt(i) + 1];
						gfn_showMsg("CW00042", arr);
						return false;
					}
				}
				if(knd == "C") { //반송
					if(ds_list.GetColumn(i, "PST") <> "25") {
						var arr = [parseInt(i) + 1];
						gfn_showMsg("CW00042", arr);
						return false;
					}
				}
				if(knd == "D") { //승인
					if((ds_list.GetColumn(i, "ACB") == "N" && ds_list.GetColumn(i, "PST") <> "25") || 
					   (ds_list.GetColumn(i, "ACB") == "Y" && ds_list.GetColumn(i, "PST") <> "35") ||
					   (ds_list.GetColumn(i, "ACB") == "Y" && ds_list.GetColumn(i, "PST") == "35" && ds_list.GetColumn(i, "JRT") == "20")) {
						var arr = [parseInt(i) + 1];
						gfn_showMsg("CW00042", arr);
						return false;
					}
				}
				if(knd == "E") { //출력
					if(ds_list.GetColumn(i, "PST") <> "40") {
						var arr = [parseInt(i) + 1];
						gfn_showMsg("CW00042", arr);
						return false;
					}

					if(ds_list.GetColumn(i, "GBN_NM") <> "승강기") {
						gfn_showAlert("제품구분이 승강기가 아닙니다.\n출력할수 없습니다.");
						return false;
					}
				}
			} else { //진행상태가 삭제일 경우
				gfn_showAlert("[" + (parseInt(i) + 1) + "]번째 행의 진행상태가 삭제입니다.\n확인하여 주십시오.");
				return false;
			}
		}
	}

	return true;
}

function cb_vkbur_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_cond_tem.SetColumn(0, "VKGRP", substr(strCode,0,2));
	cb_vkgrp.value = "";

	if(length(trim(strCode)) == 0) {
		fn_searchVkgrpCode();
	} else {
		fn_ChgVkgrp();
	}

	ds_user.Filter("1==1");
	cb_user.Index = 0;
	cb_user.Enable = false;
}

function cb_vkgrp_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_user.Filter("1==1");
		cb_user.Index = 0;
		cb_user.Enable = false;
	} else {
		cb_user.Enable = true;
		ds_user.Filter("TEAM == " + quote(strCode));
		gfn_insertEmptyRow(ds_user, G_CODE_ALL);
		cb_user.Index = 0;
	}
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;
	switch(strColumnID) {
		case "CHK":
			if(varNewValue == '1') {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			} else {
				ds_list.SetColumn(nRow,"CHK","0");
				ds_list.SetColumn(nRow,"FLAG","");
			}
			break;
	}
	obj.fireevent = true;
}
