//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm();

	if(gubun == 'I') {
		ds_cond2.SetColumn(0,"DEP","");
		ds_cond2.SetColumn(0,"RDT","");
		ds_cond2.SetColumn(0,"SEQ","");
		ds_cond2.SetColumn(0,"SIR","");
		ds_cond3.SetColumn(0,"TEM",ds_list.GetColumn(ds_list.row,"TEM"));

		div_btn.btn_recost.enable = false;
		div_btn.btn_save.enable = true;
		Tab00.tab1.btn_plus.Enable = true;
		Tab00.tab1.btn_minus.Enable = true;
	} else if(gubun == 'U') {
		ds_cond2.SetColumn(0,"DEP",ds_list.GetColumn(ds_list.row,"DEP"));
		ds_cond2.SetColumn(0,"RDT",ds_list.GetColumn(ds_list.row,"RDT"));
		ds_cond2.SetColumn(0,"SEQ",ds_list.GetColumn(ds_list.row,"SEQ"));
		ds_cond2.SetColumn(0,"SIR",ds_list.GetColumn(ds_list.row,"SIR"));
		ds_cond3.SetColumn(0,"TEM",ds_list.GetColumn(ds_list.row,"TEM"));

		if(ds_list.GetColumn(ds_list.row,"PST") == '10' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //작성
			div_btn.btn_recost.enable = false;
			div_btn.btn_save.enable = true;
			Tab00.tab1.btn_plus.Enable = true;
			Tab00.tab1.btn_minus.Enable = true;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '15' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //대리점반송
			div_btn.btn_recost.enable = false;
			div_btn.btn_save.enable = true;
			Tab00.tab1.btn_plus.Enable = true;
			Tab00.tab1.btn_minus.Enable = true;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '20' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //견적반송
			div_btn.btn_recost.enable = false;
			div_btn.btn_save.enable = true;
			Tab00.tab1.btn_plus.Enable = true;
			Tab00.tab1.btn_minus.Enable = true;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '25' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //전송
			div_btn.btn_recost.enable = false;
			div_btn.btn_save.enable = false;
			Tab00.tab1.btn_plus.Enable = false;
			Tab00.tab1.btn_minus.Enable = false;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '30' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //접수
			div_btn.btn_recost.enable = false;
			div_btn.btn_save.enable = false;
			Tab00.tab1.btn_plus.Enable = false;
			Tab00.tab1.btn_minus.Enable = false;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '35' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //확정
			if(ds_list.GetColumn(ds_list.row,"JRT") == '20') { //판정결과 : 불가
				div_btn.btn_recost.enable = true;
			} else { //판정결과 : 승인
				div_btn.btn_recost.enable = false;
			}
			div_btn.btn_save.enable = false;
			Tab00.tab1.btn_plus.Enable = true;
			Tab00.tab1.btn_minus.Enable = true;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '40' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //승인
			div_btn.btn_recost.enable = true;
			div_btn.btn_save.enable = false;
			Tab00.tab1.btn_plus.Enable = true;
			Tab00.tab1.btn_minus.Enable = true;
		} else if(ds_list.GetColumn(ds_list.row,"PST") == '60' && ds_list.GetColumn(ds_list.row,"DEL") == 'N') { //승인
			div_btn.btn_recost.enable = true;
			div_btn.btn_save.enable = false;
			Tab00.tab1.btn_plus.Enable = true;
			Tab00.tab1.btn_minus.Enable = true;
		} else {
			div_btn.btn_recost.enable = false;
			div_btn.btn_save.enable = false;
			Tab00.tab1.btn_plus.Enable = false;
			Tab00.tab1.btn_minus.Enable = false;
		}

		cal_cdt.Enable = false;
		cb_tem.Enable = false;
		cb_userp.Enable = false;
	} else if(gubun == 'S') {
		if(code == "BS05") {
			ds_cond2.SetColumn(0,"DEP",ds_list.GetColumn(ds_list.row,"DEP"));
			ds_cond2.SetColumn(0,"RDT",ds_list.GetColumn(ds_list.row,"RDT"));
			ds_cond2.SetColumn(0,"SEQ",ds_list.GetColumn(ds_list.row,"SEQ"));
			ds_cond2.SetColumn(0,"SIR",ds_list.GetColumn(ds_list.row,"SIR"));
			ds_cond3.SetColumn(0,"TEM",ds_list.GetColumn(ds_list.row,"TEM"));
		} else {
			ds_cond2.SetColumn(0,"DEP",ds_list.GetColumn(ds_list.row,"BS201_DEP"));
			ds_cond2.SetColumn(0,"RDT",ds_list.GetColumn(ds_list.row,"BS201_RDT"));
			ds_cond2.SetColumn(0,"SEQ",ds_list.GetColumn(ds_list.row,"BS201_SEQ"));
			ds_cond2.SetColumn(0,"SIR",ds_list.GetColumn(ds_list.row,"BS201_SIR"));
			ds_cond3.SetColumn(0,"TEM",ds_list.GetColumn(ds_list.row,"BS201_TEM"));
		}

		div_btn.btn_recost.enable = false;
		div_btn.btn_save.enable = false;
		Tab00.tab1.btn_plus.Enable = false;
		Tab00.tab1.btn_minus.Enable = false;

		cal_cdt.Enable = false;
		cb_tem.Enable = false;
		cb_userp.Enable = false;
	} else {
		ds_cond2.SetColumn(0,"DEP","");
		ds_cond2.SetColumn(0,"RDT","");
		ds_cond2.SetColumn(0,"SEQ","");
		ds_cond2.SetColumn(0,"SIR","");
		ds_cond3.SetColumn(0,"TEM",ds_list.GetColumn(ds_list.row,"TEM"));

		div_btn.btn_recost.enable = false;
		div_btn.btn_save.enable = false;
		Tab00.tab1.btn_plus.Enable = false;
		Tab00.tab1.btn_minus.Enable = false;
	}

	var codeArr = ["BS402","BS403","BS404","BS404","BS404","BS404","VKBUR","VKGRP","BS406","BS406","BS406","BS406","BS407"];
	var dsObjArr = [ds_gbn2,ds_buse,ds_sukum1,ds_sukum2,ds_sukum3,ds_sukum4,ds_dpt,ds_tem,ds_sgn1,ds_sgn2,ds_sgn3,ds_sgn4,ds_pst];
	var addTypeArr = [G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_NONE,G_CODE_NONE,G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_NONE];
	var oderTypeArr = [];
	gfn_searchCommCode2(codeArr, dsObjArr, addTypeArr, oderTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs05:BS0501002A_S1");
	tit_addSearchActionInfo("bs05:BS0501002A_S2");
	tit_addSearchActionInfo("bs05:BS0501002A_S3");
	tit_addSearchActionInfo("bs05:BS0501002A_S4");
	tit_addSearchActionInfo("bs05:BS0501002A_S5");
	tit_addSearchActionInfo("bs05:BS0501002A_S6");
	tit_addSearchActionInfo("bs05:BS0501002A_S7");
	tit_addSearchActionInfo("bs05:BS0501002A_S8");

	tit_callService(
        ""
        , ""
        , "ds_cond3=ds_cond3"
        , "ds_userp=ds_userp ds_mine=ds_mine ds_acd1=ds_acd1 ds_atyp=ds_atyp ds_ause=ds_ause ds_aman=ds_aman ds_aopn=ds_aopn ds_aspd=ds_aspd"
        , ""
        , "fn_afterUser"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
//	ds_riv.Filter("CODE <> '10'");
	Tab00.tab3.cb_c01.Index = 0;
	Tab00.tab3.cb_c02.Index = 0;
	Tab00.tab3.cb_c03.Index = 0;
	Tab00.tab3.cb_c04.Index = 0;

	Tab00.tab3.cb_g01.Index = 0;
	Tab00.tab3.cb_g02.Index = 0;
	Tab00.tab3.cb_g03.Index = 0;
	Tab00.tab3.cb_g04.Index = 0;
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs05:BS0501002A_S9");
	tit_addSearchActionInfo("bs05:BS0501002A_S10");

	tit_callService(
        ""
        , ""
        , "ds_cond2=ds_cond2"
        , "ds_head=ds_head ds_list2=ds_list2"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

