/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterUser(errCode, errMsg) {
	gfn_insertEmptyRow(ds_userp, G_CODE_SELECT);

	if(gubun == "I") {
		ds_tem.Filter("SUBSTR(CODE,0,2) == " + quote(substr(cb_dpt.Value,0,2)));

		cb_dpt.value = ds_mine.GetColumn(0, "DPT");
		cb_tem.value = ds_mine.GetColumn(0, "TEM");
		cal_cdt.value = gfn_getCurrDate();

		//cb_userp.Index = 0;
		cb_gbn2.Index = 1; //제품구분
		cb_gbn3.Index = 1; //견적구분(대리점용)
		cb_buse.Index = 0; //건물용도
		cb_wgbn.Index = 1; //업무구분
		cb_abl.Index = 0;

		fn_teamUser();
	} else {
		fn_query();
	}
}

function fn_teamUser(idx) {
	ds_cond3.SetColumn(0,"TEM",ds_mine.GetColumn(0, "TEM"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs05:BS0501002A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond3=ds_cond3"
		, "ds_userp=ds_userp"
		, ""
		, "fn_afterTeamUser"
		, true
		, true);
}

function fn_afterTeamUser(errCode, errMsg) {
	gfn_insertEmptyRow(ds_userp, G_CODE_SELECT);
	cb_userp.value = G_USER_ID;
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(ds_head.GetColumn(0, "PST") >= '25') {
		gfn_showAlert("견적이 이미 전송되어 저장이 불가능 합니다.");
		return;
	}

	if(gfn_isNull(ed_gnm, true, ed_gnm.Value)) return false; // 발주처
	if(gfn_isNull(ed_spt, true, ed_spt.Value)) return false; // 공사명
	if(gfn_isNull(ed_dev, true, ed_dev.Value)) return false; // 시행사
	if(gfn_isNull(ed_tel, true, ed_tel.Value)) return false; // 현장연락처
	if(gfn_isNull(med_zipcode2, true, med_zipcode2.Value)) return false; // 우편번호
	if(gfn_isNull(ed_addr2, true, ed_addr2.Value)) return false; // 주소1
	if(gfn_isNull(ed_dev, true, ed_dev.Value)) return false; // 시행사

	if(length(trim(cal_cdt.Value)) == 0) { // 견적등록일
		gfn_showMsg("견적등록일은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_userp.Value)) == 0) { // 견적담당자
		gfn_showMsg("견적담당자는 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_wgbn.Value)) == 0) { // 업무구분
		gfn_showMsg("업무구분은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_gbn2.Value)) == 0) { // 제품구분
		gfn_showMsg("제품구분은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_buse.Value)) == 0) { // 건물용도
		gfn_showMsg("건물용도는 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cal_ydt.Value)) == 0) { // 납기예정일
		gfn_showMsg("납기예정일은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cal_idt.Value)) == 0) { // 입찰예정일
		gfn_showMsg("입찰예정일은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_gbn3.Value)) == 0) { // 견적구분
		gfn_showMsg("견적구분은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_abl.Value)) == 0) { // 수주가능성
		gfn_showMsg("수주가능성은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	// 사양및견적정보 필수입력 체크
	var dsArr = ["CDG","MTN","TYP","USE","SPD","STP","STQ","OAM","AMT","QTY"];
	var lbArr = [st_cdg.value,st_mtn.value,st_typ.value,st_use.value,st_spd.value,st_stp.value,st_stq.value,st_goam.value,st_gamt.value,st_gqty.value];

	if(gfn_isGridNull(Tab00.tab1.grd_list2,dsArr,lbArr,"")) {
		return;
	}
/*
	if(length(ToString(Tab00.tab3.ed_p01.Text)) == 0) {
		gfn_showAlert("계약보증금 항목은 필수입력 항목입니다.\n확인하여 주십시오.");
		return false;
	}
	if(length(ToString(Tab00.tab3.ed_p02.Text)) == 0) {
		gfn_showAlert("1차중도금 항목은 필수입력 항목입니다.\n확인하여 주십시오.");
		return false;
	}
	if(length(ToString(Tab00.tab3.ed_p04.Text)) == 0) {
		gfn_showAlert("잔금 항목은 필수입력 항목입니다.\n확인하여 주십시오.");
		return false;
	}
*/
	if(parseInt(Tab00.tab3.ed_p01.Text)+parseInt(Tab00.tab3.ed_p02.Text)+parseInt(Tab00.tab3.ed_p03.Text)+parseInt(Tab00.tab3.ed_p04.Text) < 100) {
		gfn_showAlert("지불조건 합이 100보다 작습니다.\n확인하여 주십시오.");
		return false;
	}

	if(parseInt(Tab00.tab3.ed_p01.Text)+parseInt(Tab00.tab3.ed_p02.Text)+parseInt(Tab00.tab3.ed_p03.Text)+parseInt(Tab00.tab3.ed_p04.Text) > 100) {
		gfn_showAlert("지불조건 합이 100을 넘을수 없습니다.\n확인하여 주십시오.");
		return false;
	}

	if(Tab00.tab3.ed_p01.Text <> '0' && length(ToString(Tab00.tab3.ed_p01.Text)) > 0 && (length(Tab00.tab3.cb_c01.Value) == 0 || length(Tab00.tab3.cb_g01.Value) == 0)) {
		gfn_showAlert("계약보증금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}
	if(Tab00.tab3.ed_p02.Text <> '0' && length(ToString(Tab00.tab3.ed_p02.Text)) > 0 && (length(Tab00.tab3.cb_c02.Value) == 0 || length(Tab00.tab3.cb_g02.Value) == 0)) {
		gfn_showAlert("1차중도금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}
	if(Tab00.tab3.ed_p03.Text <> '0' && length(ToString(Tab00.tab3.ed_p03.Text)) > 0 && (length(Tab00.tab3.cb_c03.Value) == 0 || length(Tab00.tab3.cb_g03.Value) == 0)) {
		gfn_showAlert("2차중도금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}
	if(Tab00.tab3.ed_p04.Text <> '0' && length(ToString(Tab00.tab3.ed_p04.Text)) > 0 && (length(Tab00.tab3.cb_c04.Value) == 0 || length(Tab00.tab3.cb_g04.Value) == 0)) {
		gfn_showAlert("잔금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}

	var sum_oam, sum_amt, sum_qty, sum_gam, spc, acb;
	var hsd = 0;

	for(var i=0; i<ds_list2.rowcount; i++) {
		ds_list2.SetColumn(i, "DEP", cb_wgbn.Value);

		if(gubun == 'I') {
			ds_list2.SetColumn(i, "RDT", cal_cdt.Value);
			ds_list2.SetColumn(i, "SEQ", "");
			ds_list2.SetColumn(i, "SIR", "");
			ds_list2.SetColumn(i, "ISR", "");
		} else {
			ds_list2.SetColumn(i, "RDT", ds_list.GetColumn(ds_list.row,"RDT"));
			ds_list2.SetColumn(i, "SEQ", ds_list.GetColumn(ds_list.row,"SEQ"));
			ds_list2.SetColumn(i, "SIR", ds_list.GetColumn(ds_list.row,"SIR"));
		}

		if(substr(ds_list2.GetColumn(i, "MTN"),0,1) == "L") { //EL
			if(length(trim(ds_list2.GetColumn(i, "MAN"))) == 0) {
				gfn_showAlert("인승항목이 누락되었습니다.\n확인하여 주십시오.");
				return false;
			}

			if(length(trim(ds_list2.GetColumn(i, "OPN"))) == 0) {
				gfn_showAlert("열림항목이 누락되었습니다.\n확인하여 주십시오.");
				return false;
			}

			//용도 + 인승(용량) + '-' +OPEN 타입 + 속도 + '-' + 층수 + '/' + 정지층수
			spc = ds_list2.GetColumn(i, "USE") + ds_list2.GetColumn(i, "MAN")
					+ '-' + ds_list2.GetColumn(i, "OPN") + ds_list2.GetColumn(i, "SPD") 
					+ '-' + ds_list2.GetColumn(i, "STP");

			if(parseInt(ds_list2.GetColumn(i, "STP")) <> parseInt(ds_list2.GetColumn(i, "STQ"))) {
				spc = spc + '/' + ds_list2.GetColumn(i, "STQ");
			}
		} else if(substr(ds_list2.GetColumn(i, "MTN"),0,1) == "S") { //ES
			//폭(너비) + '-' + 층고 + '-' + 각도
			spc = ds_list2.GetColumn(i, "SPD") + '-' + ds_list2.GetColumn(i, "STQ")
					+ '-' + ds_list2.GetColumn(i, "USE");
		} else if(substr(ds_list2.GetColumn(i, "MTN"),0,1) == "W") { //MW
			//각도 > 0  --> 폭(너비) + '-' + 층고 + '-' + 각도
			//각도 = 0  --> 폭(너비) + '-' + 길이
			if(parseInt(ds_list2.GetColumn(i, "USE"))) {
				spc = ds_list2.GetColumn(i, "SPD") + '-' + ds_list2.GetColumn(i, "STQ")
						+ '-' + ds_list2.GetColumn(i, "USE");
			} else {
				spc = ds_list2.GetColumn(i, "SPD") + '-' + ds_list2.GetColumn(i, "STQ");
			}
		} else {
			//용도 + 인승(용량) + '-' +OPEN 타입 + 속도 + '-' + 층수 + '/' + 정지층수
			spc = ds_list2.GetColumn(i, "USE") + ds_list2.GetColumn(i, "MAN")
					+ '-' + ds_list2.GetColumn(i, "OPN") + ds_list2.GetColumn(i, "SPD") 
					+ '-' + ds_list2.GetColumn(i, "STP");

			if(parseInt(ds_list2.GetColumn(i, "STP")) <> parseInt(ds_list2.GetColumn(i, "STQ"))) {
				spc = spc + '/' + ds_list2.GetColumn(i, "STQ");
			}
		}

		ds_list2.SetColumn(i, "SPC", spc);

		sum_oam = sum_oam + parseFloat(ds_list2.GetColumn(i, "OAM")) * parseFloat(ds_list2.GetColumn(i, "QTY"));
		sum_amt = sum_amt + parseFloat(ds_list2.GetColumn(i, "AMT"));
		sum_qty = sum_qty + parseFloat(ds_list2.GetColumn(i, "QTY"));
		sum_gam = sum_gam + parseFloat(ds_list2.GetColumn(i, "GAM"));

		if(parseInt(ds_list2.GetColumn(i, "SPD")) > 105) {
			hsd++;
		}
	}

	if(parseInt(sum_qty) < 4 && parseInt(hsd) == 0) {
		acb = "Y";
	} else {
		acb = "N";
	}

	ds_head.SetColumn(0, "OAM", sum_oam);
	ds_head.SetColumn(0, "AMT", sum_amt);
	ds_head.SetColumn(0, "QTY", sum_qty);
	ds_head.SetColumn(0, "GAM", sum_gam);
	ds_head.SetColumn(0, "RAT", round((parseFloat(sum_oam) / parseFloat(sum_gam) * 100),1));

	tit_clearActionInfo();

	if(gubun == 'I') {
		ds_head.SetColumn(0, "DPT", cb_dpt.Value);
//		ds_head.SetColumn(0, "TEM", cb_tem.Value);
		ds_head.SetColumn(0, "TEM", ds_mine.GetColumn(0, "TEM"));
		ds_head.SetColumn(0, "PSN", cb_userp.Value);
		ds_head.SetColumn(0, "RDT", cal_cdt.Value);
		ds_head.SetColumn(0, "SEQ", "");
		ds_head.SetColumn(0, "SIR", "");
		ds_head.SetColumn(0, "MNO", "");
		ds_head.SetColumn(0, "GNM", ed_gnm.Text);
		if(chk_cst.Value == true) {
			ds_head.SetColumn(0, "CST", "Y");
		} else {
			ds_head.SetColumn(0, "CST", "N");
		}
		ds_head.SetColumn(0, "SPT", ed_spt.Text);
		ds_head.SetColumn(0, "DEV", ed_dev.Text);
		ds_head.SetColumn(0, "PID", ed_pid.Text);
		ds_head.SetColumn(0, "ZIP", med_zipcode2.Text);
		ds_head.SetColumn(0, "AD1", ed_addr2.Text);
		ds_head.SetColumn(0, "AD2", ed_addrdetail2.Text);
		ds_head.SetColumn(0, "TEL", ed_tel.Text);
		ds_head.SetColumn(0, "DEP", cb_wgbn.Value);
		ds_head.SetColumn(0, "GBN", cb_gbn2.Value);
		ds_head.SetColumn(0, "AGB", cb_gbn3.Value);
		ds_head.SetColumn(0, "USE", cb_buse.Value);
		ds_head.SetColumn(0, "YDT", cal_ydt.Value);
		ds_head.SetColumn(0, "IDT", cal_idt.Value);
		ds_head.SetColumn(0, "ABL", cb_abl.Value);
		if(chk_cst.Value == true) { //기거래선
			if(cb_dpt.Value <= '2100') { //지역 : 서울
				ds_head.SetColumn(0, "ACB", "N");
			} else {
				if(acb == "Y") { //3대이하, 속도 105이하
					ds_head.SetColumn(0, "ACB", "Y");
				} else {
					ds_head.SetColumn(0, "ACB", "N");
				}
			}
		} else {
			if(acb == "Y") {
				ds_head.SetColumn(0, "ACB", "Y");
			} else {
				ds_head.SetColumn(0, "ACB", "N");
			}
		}
		if(chk_ri1.Value == true) {
			ds_head.SetColumn(0, "RI1", "20"); //오티스
		}
		if(chk_ri2.Value == true) {
			ds_head.SetColumn(0, "RI2", "30"); //티센
		}
		if(chk_ri3.Value == true) {
			ds_head.SetColumn(0, "RI3", "40"); //쉰들러
		}
		if(chk_ri4.Value == true) {
			ds_head.SetColumn(0, "RI4", "50"); //꼬네
		}
		if(chk_ri5.Value == true) {
			ds_head.SetColumn(0, "RI5", "60"); //미쓰비시
		}
		if(chk_ri6.Value == true) {
			ds_head.SetColumn(0, "RI6", "70"); //기타
		}
		ds_head.SetColumn(0, "T01", Tab00.tab2.ed_t01.Text);
		ds_head.SetColumn(0, "T02", Tab00.tab2.ed_t02.Text);
		ds_head.SetColumn(0, "T03", Tab00.tab2.ed_t03.Text);
		ds_head.SetColumn(0, "T04", Tab00.tab2.ed_t04.Text);
		ds_head.SetColumn(0, "T05", Tab00.tab2.ed_t05.Text);
		ds_head.SetColumn(0, "T06", Tab00.tab2.ed_t06.Text);
		ds_head.SetColumn(0, "T07", Tab00.tab2.ed_t07.Text);
		ds_head.SetColumn(0, "T08", Tab00.tab2.ed_t08.Text);
		ds_head.SetColumn(0, "T09", Tab00.tab2.ed_t09.Text);
		ds_head.SetColumn(0, "T10", Tab00.tab2.ed_t10.Text);
		if(length(trim(Tab00.tab3.ed_p01.Text)) == 0) {
			ds_head.SetColumn(0, "P01", 0);
		} else {
			ds_head.SetColumn(0, "P01", Tab00.tab3.ed_p01.Text);
		}
		if(length(trim(Tab00.tab3.ed_p02.Text)) == 0) {
			ds_head.SetColumn(0, "P02", 0);
		} else {
			ds_head.SetColumn(0, "P02", Tab00.tab3.ed_p02.Text);
		}
		if(length(trim(Tab00.tab3.ed_p03.Text)) == 0) {
			ds_head.SetColumn(0, "P03", 0);
		} else {
			ds_head.SetColumn(0, "P03", Tab00.tab3.ed_p03.Text);
		}
		if(length(trim(Tab00.tab3.ed_p04.Text)) == 0) {
			ds_head.SetColumn(0, "P04", 0);
		} else {
			ds_head.SetColumn(0, "P04", Tab00.tab3.ed_p04.Text);
		}
		ds_head.SetColumn(0, "C01", Tab00.tab3.cb_c01.Value);
		ds_head.SetColumn(0, "C02", Tab00.tab3.cb_c02.Value);
		ds_head.SetColumn(0, "C03", Tab00.tab3.cb_c03.Value);
		ds_head.SetColumn(0, "C04", Tab00.tab3.cb_c04.Value);
		ds_head.SetColumn(0, "G01", Tab00.tab3.cb_g01.Value);
		ds_head.SetColumn(0, "G02", Tab00.tab3.cb_g02.Value);
		ds_head.SetColumn(0, "G03", Tab00.tab3.cb_g03.Value);
		ds_head.SetColumn(0, "G04", Tab00.tab3.cb_g04.Value);

		tit_addSingleActionInfo("bs05:BS0501002A_I1");
		tit_addMultiActionInfo("bs05:BS0501002A_I2");
	} else if(gubun == 'U') {
		ds_head.SetColumn(0, "GNM", ed_gnm.Text);
		if(chk_cst.Value == true) {
			ds_head.SetColumn(0, "CST", "Y");
		} else {
			ds_head.SetColumn(0, "CST", "N");
		}
		ds_head.SetColumn(0, "SPT", ed_spt.Text);
		ds_head.SetColumn(0, "DEV", ed_dev.Text);
		ds_head.SetColumn(0, "PID", ed_pid.Text);
		ds_head.SetColumn(0, "ZIP", med_zipcode2.Text);
		ds_head.SetColumn(0, "AD1", ed_addr2.Text);
		ds_head.SetColumn(0, "AD2", ed_addrdetail2.Text);
		ds_head.SetColumn(0, "TEL", ed_tel.Text);
		ds_head.SetColumn(0, "DEP", cb_wgbn.Value);
		ds_head.SetColumn(0, "GBN", cb_gbn2.Value);
		ds_head.SetColumn(0, "AGB", cb_gbn3.Value);
		ds_head.SetColumn(0, "USE", cb_buse.Value);
		ds_head.SetColumn(0, "YDT", cal_ydt.Value);
		ds_head.SetColumn(0, "IDT", cal_idt.Value);
		ds_head.SetColumn(0, "ABL", cb_abl.Value);
		if(chk_cst.Value == true) { //기거래선
			if(cb_dpt.Value <= '2100') { //지역 : 서울
				ds_head.SetColumn(0, "ACB", "N");
			} else {
				if(acb == "Y") { //3대이하, 속도 105이하
					ds_head.SetColumn(0, "ACB", "Y");
				} else {
					ds_head.SetColumn(0, "ACB", "N");
				}
			}
		} else {
			if(acb == "Y") {
				ds_head.SetColumn(0, "ACB", "Y");
			} else {
				ds_head.SetColumn(0, "ACB", "N");
			}
		}
		if(chk_ri1.Value == true) {
			ds_head.SetColumn(0, "RI1", "20"); //오티스
		}
		if(chk_ri2.Value == true) {
			ds_head.SetColumn(0, "RI2", "30"); //티센
		}
		if(chk_ri3.Value == true) {
			ds_head.SetColumn(0, "RI3", "40"); //쉰들러
		}
		if(chk_ri4.Value == true) {
			ds_head.SetColumn(0, "RI4", "50"); //꼬네
		}
		if(chk_ri5.Value == true) {
			ds_head.SetColumn(0, "RI5", "60"); //미쓰비시
		}
		if(chk_ri6.Value == true) {
			ds_head.SetColumn(0, "RI6", "70"); //기타
		}
		ds_head.SetColumn(0, "T01", Tab00.tab2.ed_t01.Text);
		ds_head.SetColumn(0, "T02", Tab00.tab2.ed_t02.Text);
		ds_head.SetColumn(0, "T03", Tab00.tab2.ed_t03.Text);
		ds_head.SetColumn(0, "T04", Tab00.tab2.ed_t04.Text);
		ds_head.SetColumn(0, "T05", Tab00.tab2.ed_t05.Text);
		ds_head.SetColumn(0, "T06", Tab00.tab2.ed_t06.Text);
		ds_head.SetColumn(0, "T07", Tab00.tab2.ed_t07.Text);
		ds_head.SetColumn(0, "T08", Tab00.tab2.ed_t08.Text);
		ds_head.SetColumn(0, "T09", Tab00.tab2.ed_t09.Text);
		ds_head.SetColumn(0, "T10", Tab00.tab2.ed_t10.Text);
		if(length(trim(Tab00.tab3.ed_p01.Text)) == 0) {
			ds_head.SetColumn(0, "P01", 0);
		} else {
			ds_head.SetColumn(0, "P01", Tab00.tab3.ed_p01.Text);
		}
		if(length(trim(Tab00.tab3.ed_p02.Text)) == 0) {
			ds_head.SetColumn(0, "P02", 0);
		} else {
			ds_head.SetColumn(0, "P02", Tab00.tab3.ed_p02.Text);
		}
		if(length(trim(Tab00.tab3.ed_p03.Text)) == 0) {
			ds_head.SetColumn(0, "P03", 0);
		} else {
			ds_head.SetColumn(0, "P03", Tab00.tab3.ed_p03.Text);
		}
		if(length(trim(Tab00.tab3.ed_p04.Text)) == 0) {
			ds_head.SetColumn(0, "P04", 0);
		} else {
			ds_head.SetColumn(0, "P04", Tab00.tab3.ed_p04.Text);
		}
		ds_head.SetColumn(0, "C01", Tab00.tab3.cb_c01.Value);
		ds_head.SetColumn(0, "C02", Tab00.tab3.cb_c02.Value);
		ds_head.SetColumn(0, "C03", Tab00.tab3.cb_c03.Value);
		ds_head.SetColumn(0, "C04", Tab00.tab3.cb_c04.Value);
		ds_head.SetColumn(0, "G01", Tab00.tab3.cb_g01.Value);
		ds_head.SetColumn(0, "G02", Tab00.tab3.cb_g02.Value);
		ds_head.SetColumn(0, "G03", Tab00.tab3.cb_g03.Value);
		ds_head.SetColumn(0, "G04", Tab00.tab3.cb_g04.Value);

		tit_addSingleActionInfo("bs05:BS0501002A_U1");
		tit_addSingleActionInfo("bs05:BS0501002A_D1");
		tit_addMultiActionInfo("bs05:BS0501002A_I3");
	}

	tit_callService(
		""
		, ""
		, "ds_head=ds_head ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		close();
	}
}

/********************************************************************************
* 기      능   : 재견적
********************************************************************************/
function fn_recost() {
	if(ds_head.GetColumn(0, "ACB") == "N" && (ds_head.GetColumn(0, "PST") <> '40' && ds_head.GetColumn(0, "PST") <> '60')) {
		gfn_showAlert("재견적은 승인 또는 완료상태에서 가능합니다.");
		return;
	}

	if(ds_head.GetColumn(0, "ACB") == "Y" && ds_head.GetColumn(0, "JRT") == "10" && ds_head.GetColumn(0, "PST") <> '40') {
		gfn_showAlert("재견적은 승인상태에서 가능합니다.");
		return;
	}

	if(ds_head.GetColumn(0, "ACB") == "Y" && ds_head.GetColumn(0, "JRT") == "20" && ds_head.GetColumn(0, "PST") <> '35') {
		gfn_showAlert("재견적은 확정상태에서 가능합니다.");
		return;
	}

	if(parseInt(ds_list.GetColumn(ds_list.row,"RNO")) > 1) {
		gfn_showAlert("재견적은 최종차수만 가능합니다.");
		return;
	}

	if(gfn_isNull(ed_gnm, true, ed_gnm.Value)) return false; // 발주처
	if(gfn_isNull(ed_spt, true, ed_spt.Value)) return false; // 공사명
	if(gfn_isNull(ed_dev, true, ed_dev.Value)) return false; // 시행사
	if(gfn_isNull(ed_tel, true, ed_tel.Value)) return false; // 현장연락처
	if(gfn_isNull(med_zipcode2, true, med_zipcode2.Value)) return false; // 우편번호
	if(gfn_isNull(ed_addr2, true, ed_addr2.Value)) return false; // 주소1
	if(gfn_isNull(ed_dev, true, ed_dev.Value)) return false; // 시행사

	if(length(trim(cal_cdt.Value)) == 0) { // 견적등록일
		gfn_showMsg("견적등록일은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_userp.Value)) == 0) { // 견적담당자
		gfn_showMsg("견적담당자는 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_wgbn.Value)) == 0) { // 업무구분
		gfn_showMsg("업무구분은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_gbn2.Value)) == 0) { // 제품구분
		gfn_showMsg("제품구분은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_buse.Value)) == 0) { // 건물용도
		gfn_showMsg("건물용도는 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cal_ydt.Value)) == 0) { // 납기예정일
		gfn_showMsg("납기예정일은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cal_idt.Value)) == 0) { // 입찰예정일
		gfn_showMsg("입찰예정일은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_gbn3.Value)) == 0) { // 견적구분
		gfn_showMsg("견적구분은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(length(trim(cb_abl.Value)) == 0) { // 수주가능성
		gfn_showMsg("수주가능성은 필수입력 항목입니다. 확인하여 주십시오.");
		return false;
	}

	if(ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	// 사양및견적정보 필수입력 체크
	var dsArr = ["CDG","MTN","TYP","USE","SPD","STP","STQ","OAM","AMT","QTY"];
	var lbArr = [st_cdg.value,st_mtn.value,st_typ.value,st_use.value,st_spd.value,st_stp.value,st_stq.value,st_goam.value,st_gamt.value,st_gqty.value];

	if(gfn_isGridNull(Tab00.tab1.grd_list2,dsArr,lbArr,"")) {
		return;
	}
/*
	if(length(ToString(Tab00.tab3.ed_p01.Text)) == 0) {
		gfn_showAlert("계약보증금 항목은 필수입력 항목입니다.\n확인하여 주십시오.");
		return false;
	}
	if(length(ToString(Tab00.tab3.ed_p02.Text)) == 0) {
		gfn_showAlert("1차중도금 항목은 필수입력 항목입니다.\n확인하여 주십시오.");
		return false;
	}
	if(length(ToString(Tab00.tab3.ed_p04.Text)) == 0) {
		gfn_showAlert("잔금 항목은 필수입력 항목입니다.\n확인하여 주십시오.");
		return false;
	}
*/
	if(parseInt(Tab00.tab3.ed_p01.Text)+parseInt(Tab00.tab3.ed_p02.Text)+parseInt(Tab00.tab3.ed_p03.Text)+parseInt(Tab00.tab3.ed_p04.Text) < 100) {
		gfn_showAlert("지불조건 합이 100보다 작습니다.\n확인하여 주십시오.");
		return false;
	}

	if(parseInt(Tab00.tab3.ed_p01.Text)+parseInt(Tab00.tab3.ed_p02.Text)+parseInt(Tab00.tab3.ed_p03.Text)+parseInt(Tab00.tab3.ed_p04.Text) > 100) {
		gfn_showAlert("지불조건 합이 100을 넘을수 없습니다.\n확인하여 주십시오.");
		return false;
	}

	if(Tab00.tab3.ed_p01.Text <> '0' && length(ToString(Tab00.tab3.ed_p01.Text)) > 0 && (length(Tab00.tab3.cb_c01.Value) == 0 || length(Tab00.tab3.cb_g01.Value) == 0)) {
		gfn_showAlert("계약보증금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}
	if(Tab00.tab3.ed_p02.Text <> '0' && length(ToString(Tab00.tab3.ed_p02.Text)) > 0 && (length(Tab00.tab3.cb_c02.Value) == 0 || length(Tab00.tab3.cb_g02.Value) == 0)) {
		gfn_showAlert("1차중도금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}
	if(Tab00.tab3.ed_p03.Text <> '0' && length(ToString(Tab00.tab3.ed_p03.Text)) > 0 && (length(Tab00.tab3.cb_c03.Value) == 0 || length(Tab00.tab3.cb_g03.Value) == 0)) {
		gfn_showAlert("2차중도금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}
	if(Tab00.tab3.ed_p04.Text <> '0' && length(ToString(Tab00.tab3.ed_p04.Text)) > 0 && (length(Tab00.tab3.cb_c04.Value) == 0 || length(Tab00.tab3.cb_g04.Value) == 0)) {
		gfn_showAlert("잔금 항목 입력이 잘못되었습니다.\n확인하여 주십시오.");
		return false;
	}

	var sum_oam, sum_amt, sum_qty, sum_gam, spc, acb;
	var hsd = 0;

	for(var i=0; i<ds_list2.rowcount; i++) {
		ds_list2.SetColumn(i, "DEP", cb_wgbn.Value);

		if(gubun == 'I') {
			ds_list2.SetColumn(i, "RDT", cal_cdt.Value);
			ds_list2.SetColumn(i, "SEQ", "");
			ds_list2.SetColumn(i, "SIR", "");
			ds_list2.SetColumn(i, "ISR", "");
		} else {
			if(ds_head.GetColumn(0, "PST") == '40') { //진행상태 : 승인(견적)
				ds_list2.SetColumn(i, "RDT", ds_list.GetColumn(ds_list.row,"RDT"));
			} else if(ds_head.GetColumn(0, "PST") == '60') { //진행상태 : 완료(입찰)
				ds_list2.SetColumn(i, "RDT", gfn_getCurrDate());
			}
			ds_list2.SetColumn(i, "SEQ", ds_list.GetColumn(ds_list.row,"SEQ"));
			ds_list2.SetColumn(i, "SIR", ds_list.GetColumn(ds_list.row,"SIR"));
		}

		if(substr(ds_list2.GetColumn(i, "MTN"),0,1) == "L") { //EL
			if(length(trim(ds_list2.GetColumn(i, "MAN"))) == 0) {
				gfn_showAlert("인승항목이 누락되었습니다.\n확인하여 주십시오.");
				return false;
			}

			if(length(trim(ds_list2.GetColumn(i, "OPN"))) == 0) {
				gfn_showAlert("열림항목이 누락되었습니다.\n확인하여 주십시오.");
				return false;
			}

			//용도 + 인승(용량) + '-' +OPEN 타입 + 속도 + '-' + 층수 + '/' + 정지층수
			spc = ds_list2.GetColumn(i, "USE") + ds_list2.GetColumn(i, "MAN")
					+ '-' + ds_list2.GetColumn(i, "OPN") + ds_list2.GetColumn(i, "SPD") 
					+ '-' + ds_list2.GetColumn(i, "STP");

			if(parseInt(ds_list2.GetColumn(i, "STP")) <> parseInt(ds_list2.GetColumn(i, "STQ"))) {
				spc = spc + '/' + ds_list2.GetColumn(i, "STQ");
			}
		} else if(substr(ds_list2.GetColumn(i, "MTN"),0,1) == "S") { //ES
			//폭(너비) + '-' + 층고 + '-' + 각도
			spc = ds_list2.GetColumn(i, "SPD") + '-' + ds_list2.GetColumn(i, "STQ")
					+ '-' + ds_list2.GetColumn(i, "USE");
		} else if(substr(ds_list2.GetColumn(i, "MTN"),0,1) == "W") { //MW
			//각도 > 0  --> 폭(너비) + '-' + 층고 + '-' + 각도
			//각도 = 0  --> 폭(너비) + '-' + 길이
			if(parseInt(ds_list2.GetColumn(i, "USE"))) {
				spc = ds_list2.GetColumn(i, "SPD") + '-' + ds_list2.GetColumn(i, "STQ")
						+ '-' + ds_list2.GetColumn(i, "USE");
			} else {
				spc = ds_list2.GetColumn(i, "SPD") + '-' + ds_list2.GetColumn(i, "STQ");
			}
		} else {
			//용도 + 인승(용량) + '-' +OPEN 타입 + 속도 + '-' + 층수 + '/' + 정지층수
			spc = ds_list2.GetColumn(i, "USE") + ds_list2.GetColumn(i, "MAN")
					+ '-' + ds_list2.GetColumn(i, "OPN") + ds_list2.GetColumn(i, "SPD") 
					+ '-' + ds_list2.GetColumn(i, "STP");

			if(parseInt(ds_list2.GetColumn(i, "STP")) <> parseInt(ds_list2.GetColumn(i, "STQ"))) {
				spc = spc + '/' + ds_list2.GetColumn(i, "STQ");
			}
		}

		ds_list2.SetColumn(i, "SPC", spc);

		sum_oam = sum_oam + parseFloat(ds_list2.GetColumn(i, "OAM")) * parseFloat(ds_list2.GetColumn(i, "QTY"));
		sum_amt = sum_amt + parseFloat(ds_list2.GetColumn(i, "AMT"));
		sum_qty = sum_qty + parseFloat(ds_list2.GetColumn(i, "QTY"));
		sum_gam = sum_gam + parseFloat(ds_list2.GetColumn(i, "GAM"));

		if(parseInt(ds_list2.GetColumn(i, "SPD")) > 105) {
			hsd++;
		}
	}

	if(parseInt(sum_qty) < 4 && parseInt(hsd) == 0) {
		acb = "Y";
	} else {
		acb = "N";
	}

	ds_head.SetColumn(0, "OAM", sum_oam);
	ds_head.SetColumn(0, "AMT", sum_amt);
	ds_head.SetColumn(0, "QTY", sum_qty);
	ds_head.SetColumn(0, "GAM", sum_gam);
	ds_head.SetColumn(0, "RAT", round((parseFloat(sum_oam) / parseFloat(sum_gam) * 100),1));

	tit_clearActionInfo();

	ds_head.SetColumn(0, "DPT", cb_dpt.Value);
	//ds_head.SetColumn(0, "TEM", cb_tem.Value);
	ds_head.SetColumn(0, "TEM", ds_mine.GetColumn(0, "TEM"));
	ds_head.SetColumn(0, "PSN", cb_userp.Value);
	if(ds_head.GetColumn(0, "PST") == '40') { //진행상태 : 승인(견적)
		ds_head.SetColumn(0, "RDT", ds_head.GetColumn(0, "RDT"));
	} else if(ds_head.GetColumn(0, "PST") == '60') { //진행상태 : 완료(입찰)
		ds_head.SetColumn(0, "RDT", gfn_getCurrDate());
	}
	ds_head.SetColumn(0, "SEQ", ds_head.GetColumn(0, "SEQ"));
	ds_head.SetColumn(0, "SIR", "");
	ds_head.SetColumn(0, "MNO", "");
	ds_head.SetColumn(0, "GNM", ed_gnm.Text);
	if(chk_cst.Value == true) {
		ds_head.SetColumn(0, "CST", "Y");
	} else {
		ds_head.SetColumn(0, "CST", "N");
	}
	ds_head.SetColumn(0, "SPT", ed_spt.Text);
	ds_head.SetColumn(0, "DEV", ed_dev.Text);
	ds_head.SetColumn(0, "PID", ed_pid.Text);
	ds_head.SetColumn(0, "ZIP", med_zipcode2.Text);
	ds_head.SetColumn(0, "AD1", ed_addr2.Text);
	ds_head.SetColumn(0, "AD2", ed_addrdetail2.Text);
	ds_head.SetColumn(0, "TEL", ed_tel.Text);
	ds_head.SetColumn(0, "DEP", cb_wgbn.Value);
	ds_head.SetColumn(0, "GBN", cb_gbn2.Value);
	ds_head.SetColumn(0, "AGB", cb_gbn3.Value);
	ds_head.SetColumn(0, "USE", cb_buse.Value);
	ds_head.SetColumn(0, "YDT", cal_ydt.Value);
	ds_head.SetColumn(0, "IDT", cal_idt.Value);
	ds_head.SetColumn(0, "ABL", cb_abl.Value);
	if(chk_cst.Value == true) { //기거래선
		if(cb_dpt.Value <= '2100') { //지역 : 서울
			ds_head.SetColumn(0, "ACB", "N");
		} else {
			if(acb == "Y") { //3대이하, 속도 105이하
				ds_head.SetColumn(0, "ACB", "Y");
			} else {
				ds_head.SetColumn(0, "ACB", "N");
			}
		}
	} else {
		if(acb == "Y") {
			ds_head.SetColumn(0, "ACB", "Y");
		} else {
			ds_head.SetColumn(0, "ACB", "N");
		}
	}

	if(ds_head.GetColumn(0, "JRT") == "20") { //판정결과 : 불가(대리점 판정 포함)
		ds_head.SetColumn(0, "ACB", "Y");
	}

	if(chk_ri1.Value == true) {
		ds_head.SetColumn(0, "RI1", "20"); //오티스
	}
	if(chk_ri2.Value == true) {
		ds_head.SetColumn(0, "RI2", "30"); //티센
	}
	if(chk_ri3.Value == true) {
		ds_head.SetColumn(0, "RI3", "40"); //쉰들러
	}
	if(chk_ri4.Value == true) {
		ds_head.SetColumn(0, "RI4", "50"); //꼬네
	}
	if(chk_ri5.Value == true) {
		ds_head.SetColumn(0, "RI5", "60"); //미쓰비시
	}
	if(chk_ri6.Value == true) {
		ds_head.SetColumn(0, "RI6", "70"); //기타
	}
	ds_head.SetColumn(0, "T01", Tab00.tab2.ed_t01.Text);
	ds_head.SetColumn(0, "T02", Tab00.tab2.ed_t02.Text);
	ds_head.SetColumn(0, "T03", Tab00.tab2.ed_t03.Text);
	ds_head.SetColumn(0, "T04", Tab00.tab2.ed_t04.Text);
	ds_head.SetColumn(0, "T05", Tab00.tab2.ed_t05.Text);
	ds_head.SetColumn(0, "T06", Tab00.tab2.ed_t06.Text);
	ds_head.SetColumn(0, "T07", Tab00.tab2.ed_t07.Text);
	ds_head.SetColumn(0, "T08", Tab00.tab2.ed_t08.Text);
	ds_head.SetColumn(0, "T09", Tab00.tab2.ed_t09.Text);
	ds_head.SetColumn(0, "T10", Tab00.tab2.ed_t10.Text);
	if(length(trim(Tab00.tab3.ed_p01.Text)) == 0) {
		ds_head.SetColumn(0, "P01", 0);
	} else {
		ds_head.SetColumn(0, "P01", Tab00.tab3.ed_p01.Text);
	}
	if(length(trim(Tab00.tab3.ed_p02.Text)) == 0) {
		ds_head.SetColumn(0, "P02", 0);
	} else {
		ds_head.SetColumn(0, "P02", Tab00.tab3.ed_p02.Text);
	}
	if(length(trim(Tab00.tab3.ed_p03.Text)) == 0) {
		ds_head.SetColumn(0, "P03", 0);
	} else {
		ds_head.SetColumn(0, "P03", Tab00.tab3.ed_p03.Text);
	}
	if(length(trim(Tab00.tab3.ed_p04.Text)) == 0) {
		ds_head.SetColumn(0, "P04", 0);
	} else {
		ds_head.SetColumn(0, "P04", Tab00.tab3.ed_p04.Text);
	}
	ds_head.SetColumn(0, "C01", Tab00.tab3.cb_c01.Value);
	ds_head.SetColumn(0, "C02", Tab00.tab3.cb_c02.Value);
	ds_head.SetColumn(0, "C03", Tab00.tab3.cb_c03.Value);
	ds_head.SetColumn(0, "C04", Tab00.tab3.cb_c04.Value);
	ds_head.SetColumn(0, "G01", Tab00.tab3.cb_g01.Value);
	ds_head.SetColumn(0, "G02", Tab00.tab3.cb_g02.Value);
	ds_head.SetColumn(0, "G03", Tab00.tab3.cb_g03.Value);
	ds_head.SetColumn(0, "G04", Tab00.tab3.cb_g04.Value);

	if(ds_head.GetColumn(0, "PST") == '40') { //진행상태 : 승인(견적)
		tit_addSingleActionInfo("bs05:BS0501002A_I4");
		tit_addMultiActionInfo("bs05:BS0501002A_I5");
	} else if(ds_head.GetColumn(0, "PST") == '60') { //진행상태 : 완료(입찰)
		tit_addSingleActionInfo("bs05:BS0501002A_I1");
		tit_addMultiActionInfo("bs05:BS0501002A_I2");
	}

	tit_callService(
		""
		, ""
		, "ds_head=ds_head ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterTrans"
		, true);
}

/********************************************************************************
* 기      능   : 재견적 Callback
********************************************************************************/
function fn_afterTrans(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		close();
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(Tab00.tab1.grd_list2, true);
}

/********************************************************************************
* 기      능   : 우편번호검색 팝업
********************************************************************************/
function Button1_OnClick(obj) {
	var arg = "strObj="+quote(Obj.id);
	//gfn_openPopup("CS9601003A",true,arg);
	gfn_openPopUpUrlHdel("SES::Ses0034A.xml", true, arg);
}

/********************************************************************************
* 기      능   : 그리드에 row추가
********************************************************************************/
function btn_plus_OnClick(obj) {
	if(gubun == 'U' || gubun == 'I') {
		ds_list2.AddRow();
	}
}

/********************************************************************************
* 기      능   : 그리드에 row삭제
********************************************************************************/
function btn_minus_OnClick(obj) {
	if(gubun == 'U' || gubun == 'I') {
		ds_list2.DeleteRow(ds_list2.row);
	}
}

/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	if(ds_head.rowcount == 0 && ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113", st_param2.value);
		return;
	}

	if(ds_head.GetColumn(0, "PST") >= '40') {
		gfn_showAlert("견적이 승인되어 삭제가 불가능 합니다.");
		return;
	}

	tit_clearActionInfo();
	tit_addSingleActionInfo("bs01:BS0102001A_D1");
	tit_addSelectExActionInfo("bs01:BS0102001A_S3", "bs01:BS0102001A_D2", "");

	tit_callService(
		""
		, ""
		, "ds_head=ds_head"
		, ""
		, ""
		, "fn_afterDelete"
		, true);	
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if(strColumnID == "OAM" || strColumnID == "AMT" || strColumnID == "QTY") {
		ds_list2.FireEvent = false;
		ds_list2.SetColumn(nRow, "GAM", parseFloat(ds_list2.GetColumn(nRow, "AMT")) * parseFloat(ds_list2.GetColumn(nRow, "QTY")));
		ds_list2.SetColumn(nRow, "RAT", round((parseFloat(ds_list2.GetColumn(nRow, "OAM")) * parseFloat(ds_list2.GetColumn(nRow, "QTY")) / parseFloat(ds_list2.GetColumn(nRow, "GAM")) * 100),1));
		ds_list2.FireEvent = true;
	}
/*
	if(strColumnID == "MTN" || strColumnID == "CDG" || strColumnID == "TYP" || strColumnID == "USE" || strColumnID == "SPD") {
		ds_list2.FireEvent = false;
		if(substr(ds_list2.GetColumn(nRow, "MTN"),0,1) == "L") { //EL
			ds_acd1.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'EL'");
		} else if(substr(ds_list2.GetColumn(nRow, "MTN"),0,1) == "S") { //ES
			ds_acd1.Filter("SUBSTR(PRH,0,2) == 'ES'");
			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'ES'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'ES'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'ES'");
