			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'EL'");
		}
		ds_list2.FireEvent = true;
	}
*/
}

function Tab00_tab1_grd_list2_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell == 2 || nCell == 3 || nCell == 4 || nCell == 7) {
		ds_list2.FireEvent = false;
		if(substr(ds_list2.GetColumn(nRow, "MTN"),0,1) == "L") { //EL
			ds_acd1.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'EL'");
		} else if(substr(ds_list2.GetColumn(nRow, "MTN"),0,1) == "S") { //ES
			ds_acd1.Filter("SUBSTR(PRH,0,2) == 'ES'");
			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'ES'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'ES'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'ES'");
		} else if(substr(ds_list2.GetColumn(nRow, "MTN"),0,1) == "W") { //MW
			ds_acd1.Filter("SUBSTR(PRH,0,2) == 'MW'");
			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'MW'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'MW'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'MW'");
		} else {
			ds_acd1.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_atyp.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_ause.Filter("SUBSTR(PRH,0,2) == 'EL'");
			ds_aspd.Filter("SUBSTR(PRH,0,2) == 'EL'");
		}
		ds_list2.FireEvent = true;
	}
}

function cb_tem_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_userp.Filter("1==1");
		cb_userp.Index = 0;
	} else {
		fn_searchUserCode(nNewIndex);
	}
}

function fn_searchUserCode(idx) {
	ds_cond3.SetColumn(0,"TEM",ds_tem.GetColumn(idx,"CODE"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs05:BS0501002A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond3=ds_cond3"
		, "ds_userp=ds_userp"
		, ""
		, "fn_afterSearchUser"
		, true
		, true);
}

function fn_afterSearchUser(errCode, errMsg) {
	gfn_insertEmptyRow(ds_userp, G_CODE_SELECT);
	cb_userp.Index = 0;
}

function btn_point_add_OnClick(obj) {
	gfn_saveToExcel(Tab00.tab1.grd_list2, true);
}
