function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	var codeArr = ["BS407","BS402","BS405","BS106","BS106","VKBUR","VKGRP"];
	var dsObjArr = [ds_pst,ds_gbn,ds_rsn,ds_comp1,ds_comp2,ds_vkbur,ds_vkgrp];
	var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_SELECT,G_CODE_NONE,G_CODE_NONE,G_CODE_ALL,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	cal_fdt.Value = left(gfn_getCurrDate(),6)+"01";
	cal_tdt.Value = gfn_getCurrDate();
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	ds_pst.Filter("CODE == '' || CODE >= '40'");
	ds_comp2.Filter("CODE <> '10'");
	cb_pst.Index = 0;
	cb_gbn.Index = 0;
	cb_vkbur.Index = 0;
	cb_vkgrp.Index = 0;
	cb_user.Enable = false;
	cal_fdt.SetFocus();

	fn_searchLoginUser();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

