/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_saveTrans(gbn) {
	gfn_clearMsgBar();

	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	if(!gfn_isChangeDs(ds_list, true, "")) {
		return;
	}

	ds_list2.ClearData();
	var rows = 0;
	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i,"FLAG") == 'U') {
			if(ds_list.GetColumn(i,"PST") == "50" && gbn == "B") {
				gfn_showAlert("반송상태이므로 입찰정보를 수정후 전송하셔야 합니다.");
				return;
			}

			if(ds_list.GetColumn(i,"PST") >= "55") {
				gfn_showAlert("이미 입찰요청하셨습니다.");
				return;
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"CHK","1");
			ds_list2.SetColumn(rows,"FLAG","U");
			ds_list2.SetColumn(rows,"DEP",ds_list.GetColumn(i,"DEP"));
			ds_list2.SetColumn(rows,"RDT",ds_list.GetColumn(i,"RDT"));
			ds_list2.SetColumn(rows,"SEQ",ds_list.GetColumn(i,"SEQ"));
			ds_list2.SetColumn(rows,"SIR",ds_list.GetColumn(i,"SIR"));
			ds_list2.SetColumn(rows,"GBN",ds_list.GetColumn(i,"GBN"));
			ds_list2.SetColumn(rows,"CID",ds_list.GetColumn(i,"CID"));
			ds_list2.SetColumn(rows,"AYM",ds_list.GetColumn(i,"AYM"));
			ds_list2.SetColumn(rows,"ASQ",ds_list.GetColumn(i,"ASQ"));
			ds_list2.SetColumn(rows,"SNO",ds_list.GetColumn(i,"SNO"));
			ds_list2.SetColumn(rows,"GNM",ds_list.GetColumn(i,"GNM"));
			ds_list2.SetColumn(rows,"SPT",ds_list.GetColumn(i,"SPT"));
			ds_list2.SetColumn(rows,"QTY",ds_list.GetColumn(i,"QTY"));
			if(gbn == "A") { //저장(등록)
				ds_list2.SetColumn(rows,"PST","45");
			} else { //전송(요청)
				ds_list2.SetColumn(rows,"PST","55");
			}
			ds_list2.SetColumn(rows,"TDT",ds_list.GetColumn(i,"TDT"));
			ds_list2.SetColumn(rows,"GDT",ds_list.GetColumn(i,"GDT"));
			ds_list2.SetColumn(rows,"COA",ds_list.GetColumn(i,"COA"));
			ds_list2.SetColumn(rows,"AMA",ds_list.GetColumn(i,"AMA"));
			ds_list2.SetColumn(rows,"AMW",ds_list.GetColumn(i,"AMW"));
			ds_list2.SetColumn(rows,"AMT",ds_list.GetColumn(i,"AMT"));
			ds_list2.SetColumn(rows,"RSN",ds_list.GetColumn(i,"RSN"));
			ds_list2.SetColumn(rows,"COB",ds_list.GetColumn(i,"COB"));
			if(length(ToString(ds_list.GetColumn(i,"AMB"))) == 0) {
				ds_list2.SetColumn(rows,"AMB",0);
			} else {
				ds_list2.SetColumn(rows,"AMB",ds_list.GetColumn(i,"AMB"));
			}
			ds_list2.SetColumn(rows,"RMK",ds_list.GetColumn(i,"RMK"));
			ds_list2.SetColumn(rows,"TYP",ds_list.GetColumn(i,"TYP"));
			ds_list2.SetColumn(rows,"TU2_RATE",ds_list.GetColumn(i,"TU2_RATE"));
			ds_list2.SetColumn(rows,"SUC",ds_list.GetColumn(i,"SUC"));

			rows++;
		}	
	}

	if(ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	for(var i=0; i<ds_list2.rowcount; i++) {
		if(ds_list2.GetColumn(i,"FLAG") == 'U') {
			if(length(ds_list2.GetColumn(i,"TDT")) == 0) {
				gfn_showMsg("CW00001", st_tdt.value);
				return;
			}
			if(length(ds_list2.GetColumn(i,"GDT")) == 0) {
				gfn_showMsg("CW00001", st_gdt.value);
				return;
			}
			if(length(ds_list2.GetColumn(i,"SUC")) == 0) {
				gfn_showMsg("CW00001", st_suc.value);
				return;
			}
			if(length(ds_list2.GetColumn(i,"RSN")) == 0) {
				gfn_showMsg("CW00001", st_rsn.value);
				return;
			}
			if(ds_list2.GetColumn(i,"SUC") == "N") {
				if(length(ds_list2.GetColumn(i,"COB")) == 0) {
					gfn_showAlert("경쟁사를 입력하시기 바랍니다.");
					return;
				}
				if(ds_list2.GetColumn(i,"AMB") == 0 || length(ToString(ds_list2.GetColumn(i,"AMB"))) == 0) {
					gfn_showAlert("입찰가를 입력하시기 바랍니다.");
					return;
				}
			}
		}
	}

	tit_clearActionInfo();
	if(gbn == "A") { //저장
		tit_addSelectExActionInfo("bs05:BS0502001A_S2", "bs05:BS0502001A_I1", "bs05:BS0502001A_U1");
	} else { //전송
		tit_addMultiActionInfo("bs05:BS0502001A_U3");
	}
	tit_addMultiActionInfo("bs05:BS0502001A_U2");

	tit_callService(
		""
		, ""
		, "ds_list=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	ds_list2.ClearData();
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : DS_LIST 칼럼 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;
	switch(strColumnID) {
		case "CHK":
			if(ds_list.GetColumn(nRow,"PST") <= "50") {
				if(varNewValue == '1') {
					ds_list.SetColumn(nRow,"CHK","1");
					ds_list.SetColumn(nRow,"FLAG","U");
				} else {
					ds_list.SetColumn(nRow,"CHK","0");
					ds_list.SetColumn(nRow,"FLAG","");
				}
			} else {
				ds_list.SetColumn(nRow,"CHK","0");
				ds_list.SetColumn(nRow,"FLAG","");
			}
			break;
		case "TDT": //입찰확정일
			if(length(trim(varNewValue)) > 0) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
		case "GDT": //계약일
			if(length(trim(varNewValue)) > 0) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
		case "SUC": //성공여부
			if(length(trim(varNewValue)) > 0) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
		case "RSN": //사유
			if(varNewValue == '10') { //수의계약
				ds_list.SetColumn(nRow,"COB","");
				ds_list.SetColumn(nRow,"AMB",0);
				ds_list.SetColumn(nRow,"TU2_RATE",0.00);
			}
			if(length(trim(varNewValue)) > 0) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
		case "COB":
			if(length(trim(varNewValue)) > 0) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
/*
			if(length(varOldValue) > 0 && varOldValue <> varNewValue) {
				ds_list.SetColumn(nRow,"AMA",0);
				ds_list.SetColumn(nRow,"AMB",0);
				ds_list.SetColumn(nRow,"RSN","");
				ds_list.SetColumn(nRow,"COA","");
				ds_list.SetColumn(nRow,"TU2_RATE",0);
			}

			if(ds_list.GetColumn(nRow,"COB") == "10") {
				ds_list.SetColumn(nRow,"AMB",ds_list.GetColumn(nRow,"EAM"));
			} else if(ds_list.GetColumn(nRow,"COA") > "" && ds_list.GetColumn(nRow,"COA") <> "10") {
				ds_list.SetColumn(nRow,"AMA",ds_list.GetColumn(nRow,"EAM"));
				var rate = ROUND(ds_list.GetColumn(nRow,"OAM")/ds_list.GetColumn(nRow,"AMA")*100,2);
				ds_list.SetColumn(nRow,"TU2_RATE",rate);
			}
*/
			break;
		case "AMB": //입찰가
			var rate = ROUND(ds_list.GetColumn(nRow,"OAM")/ds_list.GetColumn(nRow,"AMB")*100,2); //당사원가 / 입찰가
			ds_list.SetColumn(nRow,"TU2_RATE",rate);
			if(varNewValue > 0 || length(trim(varNewValue)) > 0) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
	}
	obj.fireevent = true;
}

/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 60;
	if(cal_temp.top > 400) {
		cal_temp.top = 400;
	}
	cal_temp.Left = rect[0] + 10;
	if(cal_temp.Left > 790) {
		cal_temp.Left = 790;
	}

	if(ds_list.GetColumn(nRow,"PST") >= '40' && ds_list.GetColumn(nRow,"PST") < '55') {
		cal_temp.Visible = true;
	}

	if(nCell == 16) {
		cal_temp.value = ds_list.getColumn(nRow, "TDT");
	} else if(nCell == 17) {
		cal_temp.value = ds_list.getColumn(nRow, "GDT");
	}

//	cal_temp.value = ds_list.getColumn(nRow, "BS103_TDT");
	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우
********************************************************************************/
function cal_temp_OnDayClick(obj,strText) {
	if(grd_list.GetClickedCell() == 16) {
		ds_list.SetColumn(ds_list.row, "TDT", strText);
	} else if(grd_list.GetClickedCell() == 17) {
		ds_list.SetColumn(ds_list.row, "GDT", strText);
	}
	obj.Visible = false;
}

function cb_vkbur_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_cond_tem.SetColumn(0, "VKGRP", substr(strCode,0,2));
	cb_vkgrp.value = "";

	if(length(trim(strCode)) == 0) {
		fn_searchVkgrpCode();
	} else {
		fn_ChgVkgrp();
	}

	ds_user.Filter("1==1");
	cb_user.Index = 0;
	cb_user.Enable = false;
}

function cb_vkgrp_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_user.Filter("1==1");
		cb_user.Index = 0;
		cb_user.Enable = false;
	} else {
		cb_user.Enable = true;
		ds_user.Filter("TEAM == " + quote(strCode));
		gfn_insertEmptyRow(ds_user, G_CODE_ALL);
		cb_user.Index = 0;
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell == 19) {
		if(ds_list.GetColumn(nRow,"SUC") == "") {
			gfn_showAlert(parseInt(nRow + 1) + "번째 행의 성공여부를 먼저 입력하시기 바랍니다.");
			return;
		}

		if(ds_list.GetColumn(nRow,"SUC") == "Y") { //성공
			ds_rsn.Filter("1==1");
		} else { //실패
			ds_rsn.Filter("CODE <> '10'");
		}
	}
}
