// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	var codeArr = ["BS402","BS106","BS405","BS106","BS106","BS407","VKBUR","VKGRP"];
	var dsObjArr = [ds_gbn,ds_comp1,ds_rsn,ds_comp2,ds_comp3,ds_pst,ds_vkbur,ds_vkgrp];
	var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_ALL,G_CODE_ALL,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	cal_ftd.Value = left(gfn_getCurrDate(),6)+"01";
	cal_ttd.Value = gfn_getCurrDate();
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	ds_pst.Filter("CODE == '' || CODE > '40'");
	cb_pst.Index = 0;
	cb_gbn.Index = 0;
	cb_com.Index = 0;
	cb_vkbur.Index = 0;
	cb_vkgrp.Index = 0;
	cb_user.Enable = false;
	cal_fdt.SetFocus();

	fn_searchLoginUser();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

