********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 팝업연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell != 0 && nCell != 1) {   
		var arg = "gubun='S' code='BS05'";
		gfn_openPopup("BS0501002A",true,arg);
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		gfn_selectGridCol("grd_list");
	} else if(nCell == 1) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 반송
********************************************************************************/
function fn_sendback() {
	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	if(!gfn_isChangeDs(ds_list, true, "")) {
		return;
	}

	ds_list2.ClearData();
	var rows = 0;
	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i,"FLAG") == 'U') {
			if(ds_list.GetColumn(i,"PST") == "60") {
				gfn_showAlert("완료상태이므로 반송처리가 불가능합니다.");
				return;
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"CHK","1");
			ds_list2.SetColumn(rows,"FLAG","U");
			ds_list2.SetColumn(rows,"AYM",ds_list.GetColumn(i,"AYM"));
			ds_list2.SetColumn(rows,"ASQ",ds_list.GetColumn(i,"ASQ"));
			ds_list2.SetColumn(rows,"PST","50");

			rows++;
		}	
	}

	if(ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs05:BS0502002A_S2", "", "bs05:BS0502002A_U1");
	tit_addMultiActionInfo("bs05:BS0502002A_U2");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list2:A"
		, ""
		, ""
		, "fn_afterSendback");
}

/********************************************************************************
* 기      능   : 완료
********************************************************************************/
function fn_finish() {
	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	if(!gfn_isChangeDs(ds_list, true, "")) {
		return;
	}

	ds_list2.ClearData();
	var rows = 0;
	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i,"FLAG") == 'U') {
			if(ds_list.GetColumn(i,"PST") == "60") {
				gfn_showAlert("완료상태이므로 완료처리가 불가능합니다.");
				return;
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"CHK","1");
			ds_list2.SetColumn(rows,"FLAG","U");
			ds_list2.SetColumn(rows,"AYM",ds_list.GetColumn(i,"AYM"));
			ds_list2.SetColumn(rows,"ASQ",ds_list.GetColumn(i,"ASQ"));
			ds_list2.SetColumn(rows,"PST","60");

			rows++;
		}	
	}

	if(ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs05:BS0502002A_S2", "", "bs05:BS0502002A_U3");
	tit_addMultiActionInfo("bs05:BS0502002A_U2");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list2:A"
		, ""
		, ""
		, "fn_afterFinish");
}

/********************************************************************************
* 기      능   : 반송 Callback
********************************************************************************/
function fn_afterSendback(errCode, errMsg) {
	ds_list2.ClearData();
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 완료 Callback
********************************************************************************/
function fn_afterFinish(errCode, errMsg) {
	ds_list2.ClearData();
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : DS_LIST 칼럼 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;
	switch(strColumnID) {
		case "CHK":
			if(varNewValue == '1') {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			} else {
				ds_list.SetColumn(nRow,"CHK","0");
				ds_list.SetColumn(nRow,"FLAG","");
			}
			break;
	}
	obj.fireevent = true;
}

function cb_vkbur_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_cond_tem.SetColumn(0, "VKGRP", substr(strCode,0,2));
	cb_vkgrp.value = "";

	if(length(trim(strCode)) == 0) {
		fn_searchVkgrpCode();
	} else {
		fn_ChgVkgrp();
	}

	ds_user.Filter("1==1");
	cb_user.Index = 0;
	cb_user.Enable = false;
}

function cb_vkgrp_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_user.Filter("1==1");
		cb_user.Index = 0;
		cb_user.Enable = false;
	} else {
		cb_user.Enable = true;
		ds_user.Filter("TEAM == " + quote(strCode));
		gfn_insertEmptyRow(ds_user, G_CODE_ALL);
		cb_user.Index = 0;
	}
}
