var flag = false;
var hogiType = "";
var callerName = "";
var kind = "";
var cstCode = "";

var DEBUG_FLAG 	= false;														//디버그 기능 사용을 위한 플래그를 설정한다.

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();

	//if(DEBUG_FLAG) SHOW_ENV();

	gfn_initDs(ds_cond, true);

	ds_cond.SetColumn(0,"USER_GROUP",gfn_getUserGroup());
	ds_cond.SetColumn(0,"AREA",gfn_getUserArea());

    // 자재평가유형코드 DataSet 가져오기
    var codeArr = ["2"];
    var dsObjArr = [ds_bwtar_cd];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // 자재유형코드 DataSet 가져오기
    var codeArr = ["1"];
    var dsObjArr = [ds_mtart_cd];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	if (length(fa_pjt) > 0) 
	{
		callerName = fa_origin;
		kind = fa_kind;
		cstCode = fa_cst;

		if(callerName == "008C")												//재청구에서 Call 될 경우 BOM자재를 선택할 수 없게 한다.
		{
			cb_rgbn.Index = 0;
			cb_rgbn.Enable = false;
		}		
		fn_getList();
	} else {
		gfn_showAlert("CW00147");
		close();
	}
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_bwtar.Index = 0;
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
