/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	var targetQuery;

	if(toUpper(ds_ma_cond.GetColumn(0, "MATNR")) == "NS-100")
	{
		ds_ma_cond.SetColumn(0, "MATNR", "");
		gfn_showAlert("CW10025", "해당 자재는 직접 사용불가 자재입니다.");
		return;
	}

	if(cb_rgbn.Value == "1") //보수자재
	{
		targetQuery = "cs01:CS0101008B_S2";
	}
	else if(cb_rgbn.Value == "2") //내작자재
	{
		targetQuery = "cs01:CS0101008B_S10";
	}	
	else
	{
		gfn_showAlert("CW10025", "청구유형을 선택해주세요.");
		return;
	}

	if(ed_matnr.Value.length <= 0)
	{
		gfn_showAlert("CW10025", "자재번호 검색 후 이용 가능합니다.");
		return;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(targetQuery, null, true);

	tit_callService(
        ""
        , ""
        , "ds_ma_cond=ds_ma_cond"
        , "ds_ma_list=ds_ma_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   :  호기조회 
********************************************************************************/
function fn_getList() {
	ds_cond.SetColumn(0,"PJT", fa_pjt);
	//ds_cond.SetColumn(0,"CUDAT", gfn_getCurrDate());
	ds_cond.SetColumn(0,"CST", fa_cst);

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs01:CS0101008E_S1", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_plist=ds_plist"
        , ""
        , "fn_afterList"
        , true);
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if (ds_ma_list.rowcount > 0)
	{
		if (ds_ma_list.GetColumn(0,"LVORM") == 'X')
	    {
	   		gfn_showAlert("CW10025", "삭제 표기된 자재번호 입니다. 확인하십시요.");
	   		ds_ma_list.ClearData();
			return;
     	}

		ed_samt.value = ds_ma_list.GetColumn(0,"SAMT1");
		flag = true;

		if(ds_ma_list.GetColumn(0, "MTART") == "PALB"){
			ds_bwtar_cd.Filter(" CODE>=" +quote("PA") +"&&CODE<=" +quote("PR"));
			cb_bwtar.Value ="PA";
		} else if(ds_ma_list.GetColumn(0, "MTART") == "HALB") {
			ds_bwtar_cd.Filter(" CODE>=" +quote("HA") +"&&CODE<=" +quote("HR"));
			cb_bwtar.Value ="HA";
		} else if(ds_ma_list.GetColumn(0, "MTART") == "ROH"){
			ds_bwtar_cd.Filter(" CODE>=" +quote("RA") +"&&CODE<=" +quote("RR"));
			cb_bwtar.Value ="RA";
		} else {
			ds_bwtar_cd.FilterExpr = "";
		}
	}
	else
	{
		gfn_showAlert("CW10025", "존재하지 않는 자재입니다.");
		ds_ma_list.ClearData();
		ed_matnr.SetFocus();
		return;	
	}
}

function fn_afterList(errCode, errMsg) {
	if (ds_plist.rowcount < 1)
	{
		gfn_showAlert("CW00010");
		close();
	}
}

