/********************************************************************************
* 기      능   : 그리드 해드 클릭(sorting)
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectAllGrid(obj);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 자재번호 검색팝업에서 자재번호를 검색한다.
********************************************************************************/
function ufn_getMatnr(obj)
{
	var matnr = "";
	var targetPopup;
	var inputArg;

	//inputArg = "fa_hty=" + quote(hogiType);

	if(cb_rgbn.Value == "1") 													//보수자재
	{
		targetPopup = "CS9614001A";
	}
	else if(cb_rgbn.Value == "2") 												//내작자재
	{
		targetPopup = "CS9614001C";
	}	
	else
	{
		gfn_showAlert("CW10025", "청구유형을 선택해주세요.");
		return;
	}

	inputArg = "fa_hogin=" +quote(fa_pjt);
	matnr = gfn_openPopup(targetPopup, true, inputArg); 

	if (matnr.length > 0){
		ed_matnr.value = matnr;
	}
}

/********************************************************************************
* 기      능   : 자재도면을 확인한다.
********************************************************************************/
function phot_OnClick(obj,nX,nY)
{
	if (length(ed_matnr.value) > 0 && cb_rgbn.Value == "1")						//내작자재는 이미지 없음(향후 추가가능?)
	{
		gfn_openPopup("CS0301001C", true, "fa_matnr=" + quote(ed_matnr.value));
	}	
}

/********************************************************************************
* 기      능   : 해당 정보를 각 호기에 동일하게 복사한다.
********************************************************************************/
function copy_OnClick(obj)
{
	DEBUG("copy_onClick", "flag:" + flag + "/ds_plist.rowcount:" + ds_plist.rowcount);

	if (!flag )
	{
		gfn_showAlert("CW00054");
		return;
	}
	if (ds_plist.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}
	for (var i =0; i < ds_plist.rowcount; i++)
	{
		if (ds_plist.GetColumn(i, "CHECK") == "1"){
			ds_plist.SetColumn(i,"MENGE", ed_menge.Text);
		} else {
			ds_plist.SetColumn(i,"MENGE", "");
		}
	}
}

/********************************************************************************
* 기      능   : 자재유형 변경에 따른 판매단가를 다시 계산한다.
********************************************************************************/
function bwtar_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (SubStr(cb_bwtar.value,1,1) == "B") {
		ed_samt.value = ds_ma_list.GetColumn(0,"SAMT1") * 0.4;
	} else if (SubStr(cb_bwtar.value,1,1) == "A") {
		ed_samt.value = ds_ma_list.GetColumn(0,"SAMT1");
	} else {
		ed_samt.value = 0;
	}
}

/********************************************************************************
* 기      능   : 입력된 사항을 parent의 ds_list에 반영한다.
********************************************************************************/
function btn_add_OnClick(obj)
{
	if (!flag )
	{
		gfn_showAlert("CW00054");
		return;
	}

	if (ds_plist.rowcount < 1) 
	{
		gfn_showAlert("CW00013");
		return;
	}

	ds_plist.fireevent = false;
	gfn_copyDs(ds_plist, ds_plist_0, "CHECK=1 && MENGE > 0");					//선택된 행들만 복사 (수량이 0이상인 것)
	ds_plist.fireevent = true;

	if (ds_plist_0.rowcount < 1) 												// 선택된 데이터가 없으면 에러 
	{
		gfn_showAlert("CW00005");
		return;
	}

	ds_list.fireevent = false;

	for (var i = ds_list.rowcount(); i > 0; i--) 								//ds_list의 제대로 입력되지 않은 항목들을 지운다.
	{
	    if ( length(ds_list.GetColumn(ds_list.row,"MAKTX")) < 1)
	    {
			ds_list.deleteRow(ds_list.row);
	    }
	}

	for (var i = 0; i< ds_plist_0.rowcount; i++)
	{
		ds_list.AddRow();
		ufn_setMatnr(ds_plist_0.GetColumn(i, "POSID"), 							//호기번호
					 ds_plist_0.GetColumn(i, "HNA"), 							//호기명
					 ds_plist_0.GetColumn(i, "MENGE"), 							//수량
					 ds_list.row);												//ds_list의 Row
	}

	parent.ufn_checkFailSite();													//실패현장청구일 경우 세팅

	ds_list.fireevent = true;													//데이터리스트의 이벤트를 enable한다.

	parent.ufn_calcCostValues("ufn_setMatnr");									//xkwh:청구구분에 따른 계산값을 다시 계산한다.
	//fn_close();
}

/********************************************************************************
* 기      능   : 자재 정보를 parent의 ds_list에 setting한다.
********************************************************************************/
function ufn_setMatnr(hogiNo, hogiNm, matAmt, nRow)
{
	if (ds_ma_list.rowcount > 0)
	{
		ds_list.SetColumn(nRow, "MATNR", ds_ma_list.GetColumn(0,"MATNR"));		//자재번호
		ds_list.SetColumn(nRow, "MAKTX", ed_maktx.Text); 						//자재내역
		ds_list.SetColumn(nRow, "JOBHOUR", ds_ma_list.GetColumn(0,"JOBHOUR"));	//작업시간
		ds_list.SetColumn(nRow, "MAN", ds_ma_list.GetColumn(0,"MAN"));			//투입인원
		ds_list.SetColumn(nRow, "MTART", cb_mtart.Value); 						//자재유형
		ds_list.SetColumn(nRow, "GEWEI", ds_ma_list.GetColumn(0,"GEWEI")); 		//기본단위
		ds_list.SetColumn(nRow, "MEINS",ds_ma_list.GetColumn(0,"MEINS")); 		//자재단위	
		ds_list.SetColumn(nRow, "UCOSTA", ds_ma_list.GetColumn(0,"SAMT2")); 	//정품원가
		ds_list.SetColumn(nRow, "UCOSTR", ds_ma_list.GetColumn(0,"SAMT3")); 	//수리품원가
		ds_list.SetColumn(nRow, "UPR", ds_ma_list.GetColumn(0,"UPR")); 			//원가인상율
		ds_list.SetColumn(nRow, "RGBN", ds_ma_list.GetColumn(0,"RGBN")); 		//청구유형(1:보수/2:BOM)
		ds_list.SetColumn(nRow, "RGBNNM", ds_ma_list.GetColumn(0,"RGBNNM"));	//청구유형명
		ds_list.SetColumn(nRow, "BANNAB", ds_ma_list.GetColumn(0,"BANNAB")); 	//반납자재여부

		ds_list.SetColumn(nRow, "FLAG", "I");									//입력플래그
		ds_list.SetColumn(nRow, "HNO", hogiNo);									//호기번호
		ds_list.SetColumn(nRow, "HNA", hogiNm);									//호기명
		ds_list.SetColumn(nRow, "RGBN", cb_rgbn.Value);							//청구구분(보수자재/내작자재)

		if(callerName == "008C")												//재청구일 경우 해당 청구 정보를 입력한다.
		{
			ds_list.SetColumn(nRow, "CGBN", "1");								//변경구분
			ds_list.SetColumn(nRow, "CGBNNM", "재청구신규");					//변경구분명		
		}			

		ds_list.SetColumn(nRow, "CHAR1", ed_char1.Text); 						//자재특성치
		ds_list.SetColumn(nRow, "MENGE", matAmt); 								//수량
		ds_list.SetColumn(nRow, "NETPR", ed_samt.Text);							//판매가
		ds_list.SetColumn(nRow, "BWTAR", cb_bwtar.Value);						//평가유형

		if(ds_ma_list.GetColumn(0, "RGBN") == "2") 								//BOM자재이면 아래 값들을 기본값으로 넣어준다(Insert시 error방지)
		{
			ds_list.SetColumn(nRow, "BANNAB", "");								//반납자재여부(미반납)
			ds_list.SetColumn(nRow, "GEWEI", "");								//기본단위
			ds_list.SetColumn(nRow, "UCOSTR", "0");								//수리품원가
			ds_list.SetColumn(nRow, "UPR", "0"); 								//원가인상율
		}

		if ( parseInt(ds_list.GetColumn(nRow,"MENGE")) < 1)						//기초수량을 1로 세팅한다.
		{
			ds_list.SetColumn(nRow,"MENGE",1);
		}

		ds_list.SetColumn(nRow, "ZGUBN", "");									//직영재고 Index를 초기화한다.

		//xkwh:인건비 및 자재비 합계 계산을 위해 견적공수에 표준공수를 대입
		ds_list.SetColumn(nRow,"OFRHOUR",ds_ma_list.GetColumn(0,"JOBHOUR"));	//작업시간

		ds_list.SetColumn(nRow, "NETWR", ds_list.GetColumn(nRow, "MENGE") * 
											ds_list.GetColumn(nRow, "NETPR"));	//xkwh:수량*단가=합계를 계산한다.

		//xkwh:표준인건비, 계산인건비를 계산한다.
		var lcosts = toNumber(ds_list.GetColumn(nRow, "MENGE")) * 
					 toNumber(ds_list.GetColumn(nRow, "MAN")) * 
					 toNumber(ds_list.GetColumn(nRow, "JOBHOUR")) * 
					 toNumber(ds_out_master.GetColumn(0, "RATE"));
		var lcostc = toNumber(ds_list.GetColumn(nRow, "MENGE")) * 
					 toNumber(ds_list.GetColumn(nRow, "MAN")) * 
					 toNumber(ds_list.GetColumn(nRow, "OFRHOUR")) * 
					 toNumber(ds_out_master.GetColumn(0, "RATE"));

		DEBUG("ufn_setMatnr", ds_list.savexml());
		DEBUG("ufn_setMatnr", ds_out_master.savexml());

		ds_list.SetColumn(nRow, "LCOSTS", lcosts);
		ds_list.SetColumn(nRow, "LCOSTC", lcostc);

		DEBUG("ufn_setMatnr", "ds_out_master.GetColumn(0, 'rate'):" + ds_out_master.GetColumn(0, "RATE"));
		parent.ufn_calcListValues(nRow);										//xkwh:각종 계산식을 업데이트 한다.	
	}
}

/********************************************************************************
* 기      능   : 자재번호 키인 시 키인이 완료되면 바로 자재를 검색한다.
********************************************************************************/
function ed_matnr_OnKillFocus(obj)
{
	if (length(ed_matnr.value) > 0) {
		fn_query();
	}
}
