function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();

	//if(DEBUG_FLAG) SHOW_ENV();

	cal_to.Value = today();														//검색 종료일 : 오늘
	cal_from.Value = gfn_getBeforeDate(cal_to.Value, 7);						//검색 시작일 : 7일 전

	cb_gbn.Index = 0;
	cb_status.Index = 0;
}
