function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);


    // 청구종류코드,청구진행,사업장,자재평가유형코드,결재상태  DataSet 가져오기
    var codeArr = ["3","7","CS151","2","AP_GUBN", "WBAP_STAT"];
    var dsObjArr = [ds_reqkind,ds_rece_f,ds_bussi,ds_bwtar,ds_apgubn,ds_apstat];
    var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_ALL,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);


    cal_reqdate_to.value = gfn_getCurrDate();
    cal_reqdate_fm.value = gfn_getBeforeDate(cal_reqdate_to.value, 7);

    btn_serno_into.Enable = true;
    btn_serno_del.Enable = true;

    var user_grp = gfn_getUserGroup();

	if( user_grp <> "40" ){
		btn_serno_del.Enable = false;
		return;		
	}

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_rece_f.Enable = true;
	cb_reqkind.index = 0;
	cb_rece_f.index = 0;
	cb_bussi.index = 0;
	gfn_showMsg("CI00007");
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
