/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";


	if ( !gfn_checkDate( cal_reqdate_fm.value, cal_reqdate_to.value, true, st_reqdate) ) {
		return;
	}

	//조회기간: 90일 이상 조회 불가 -- 20110113 조요한 과장 요청으로 늘림
	if( (gfn_isNull(ed_pspid, false) && gfn_isNull(ed_pjt, false)) 
	    && gfn_getDayCnt(ds_days,cal_reqdate_fm.value, cal_reqdate_to.value) > 90)
	{
		gfn_showAlert("CW00078","3");
		cal_reqdate_fm.SetFocus();
		return;
	}

	var user_grp = gfn_getUserGroup();

	// if(user_grp == "40") { //고객지원부일때 조회조건중 pjt나 협력사를 선택해야 한다.
		// if(ed_pjt.value == "" && div_code.cb_mid.value == "" && div_code.cb_small.value == "") {
			// gfn_showMsg("CW00001","프로젝트번호 또는 협력사");
			// return;
		// }
	// }

	ds_cond.SetColumn(0, "GBN", ufn_getGbn()); //조회조건 구분값 가져오기
//	ds_cond.SetColumn(0, "POSID", ed_pjt.value);
	ds_cond.SetColumn(0, "CB1", div_code.cb_big.value);
	ds_cond.SetColumn(0, "CB2", div_code.cb_mid.value);
	ds_cond.SetColumn(0, "CB3", div_code.cb_small.value);

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	var qst = gfn_deptQuery();	// 부서
    tit_addSearchActionInfo("cs01:CS0102001A_S1", null, true);
    tit_addSearchActionInfo("cs01:CS0102001A_S2", null, true);
    tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_mm031=ds_mm031 ds_list=ds_list"
        , "WQST=" + quote(qst)
        , "fn_afterQuery"
        , true);
}
