/********************************************************************************
* 기      능   : Delete 25
********************************************************************************/
function fn_delete() {
	//  삭제할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");

	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	var user_grp = gfn_getUserGroup();
	var kind = "";

	if (user_grp == "10" || user_grp == "40"){  // 협력사 청구취소
		for (var i = 0; i < ds_mm031_0.rowcount ; i++)
		{
			if(ds_mm031_0.GetColumn(i,"RECE_F") != "0"){
				gfn_showAlert("CW00072");
				ds_mm031_0.SetColumn(i,"CHECK","0");
				return;
			}
			if(length(ds_mm031_0.GetColumn(i,"DOCNO")) > 0){
				gfn_showAlert("CW00064","결재문서");
				ds_mm031_0.SetColumn(i,"CHECK","0");
				return;
			}
		}

		// MMPR 데이터셋을 복사
		gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2 || REQKIND=3 || REQKIND=4");

        // 청구취소 확인
		if (!gfn_showConfirm("CI00015","청구")) {
			return;
		}
		tit_clearActionInfo();
		if (ds_mm031_1.rowcount < 1) {
			// 2015.03.04 LJH 자재청구 삭제시 견적 데이터 삭제 임시 보류(C청구 데이터 처리 문제 발생함)
			//tit_addMultiActionInfo("cs01:CS0102001A_D2");
			//tit_addMultiActionInfo("cs01:CS0102001A_D3");
		}
		if (ds_mm031_1.rowcount > 0) {
			tit_addMultiActionInfo("cs01:CS0102001A_D4");
			tit_addMultiActionInfo("cs01:CS0102001A_D5");
		}

		tit_addMultiActionInfo("cs01:CS0102001A_D1");

		tit_callService(
             ""
            , ""
			, "ds_mm031_0=ds_mm031_0:A ds_mm031_1 = ds_mm031_1:A"
			, ""
			, ""
			, "fn_afterDelete"
			, "");

	}
}
/********************************************************************************
* 기      능   : 취소 35
********************************************************************************/
function fn_cancel() {

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");

	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}




	var user_grp = gfn_getUserGroup();
	var kind = "";

	if (user_grp == "10"){  // 협력사 청구취소
		for (var i = 0; i < ds_mm031_0.rowcount ; i++)
		{
			if(ds_mm031_0.GetColumn(i,"RECE_F") != "1" && ds_mm031_0.GetColumn(i,"RECE_F") != "5"){
				gfn_showAlert("CW00071");
				ds_mm031_0.SetColumn(i,"CHECK","0");
				return;
			}
		}


        // 청구취소 확인
		if (!gfn_showConfirm("CI00013","전송")) {
			return;
		}

		for (var i = 0; i < ds_mm031_0.rowcount ; i++)
		{
			ds_mm031_0.SetColumn(i,"GBN","E");
		}

		// MMPR 데이터셋을 복사
		gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2 || REQKIND=3 || REQKIND=4");

		tit_clearActionInfo();
		tit_addMultiActionInfo("cs01:CS0102001A_U1");

		if (ds_mm031_1.rowcount < 1) {
			tit_addMultiActionInfo("cs01:CS0102001A_U2");
		}
		if (ds_mm031_1.rowcount > 0) {
			tit_addMultiActionInfo("cs01:CS0102001A_U3");
			tit_addMultiActionInfo("cs01:CS0102001A_U4");
		}

		tit_callService(
             ""
            , ""
			, "ds_mm031_0=ds_mm031_0:A ds_mm031_1 = ds_mm031_1:A"
			, ""
			, ""
			, "fn_afterCancel"
			,""
			, ""
			, "");

	} else if (user_grp == "20" || user_grp == "30"){ // 보수 pm 접수 취소

		if (!gfn_showConfirm("CI00013","접수")) {
			return;
		}

		for (var i = 0; i < ds_mm031_0.rowcount ; i++)
		{
			ds_mm031_0.SetColumn(i,"GBN","B");
		}

		// MMPR 데이터셋을 복사
		gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2  || REQKIND=3 || REQKIND=4");

		tit_clearActionInfo();
		tit_addMultiActionInfo("cs01:CS0102001A_U1");

		if (ds_mm031_1.rowcount < 1) {
			tit_addMultiActionInfo("cs01:CS0102001A_U2");
		}
		if (ds_mm031_1.rowcount > 0) {
			tit_addMultiActionInfo("cs01:CS0102001A_U3");
			tit_addMultiActionInfo("cs01:CS0102001A_U4");
		}

		if (kind == "2")  // 접수 취소시
		{

			// 취소시 작성, 접수, 승인자에게 모두 쪽지 전송 : '14.02.06 hss 이동진 요청 - START

			gfn_copyDs(ds_mm031_0, ds_mm031_3, "CHECK=1");

			for(var i = 0; i < ds_mm031_0.rowcount; i++)
			{
				ds_mm031_3.addRow();
				ds_mm031_3.copyrow(ds_mm031_3.row,"ds_mm031_0", i);
				ds_mm031_3.SetColumn(ds_mm031_3.row, "CREID", ds_mm031_0.GetColumn(i,"ACNAM"));
				if(length(ds_mm031_0.GetColumn(i,"APNAM")) > 0)
				{
					ds_mm031_3.addRow();
					ds_mm031_3.copyrow(ds_mm031_3.row,"ds_mm031_0", i);
					ds_mm031_3.SetColumn(ds_mm031_3.row, "CREID", ds_mm031_0.GetColumn(i,"APNAM"));
				}				
			}

			// 취소시 작성, 접수, 승인자에게 모두 쪽지 전송 : '14.02.06 hss 이동진 요청 - END	
			var mesgdesc = gfn_openPopup("CS0102001D", true);  // 취소 사유 쪽지 내용 등록

			if ( mesgdesc != null)
			{


				for(var i = 0; i < ds_mm031_3.rowcount; i++)
				{
					ds_mm031_3.SetColumn(i,"MESGDESC", mesgdesc);
				}
			}
			tit_addMultiActionInfo("cs01:CS0102001A_I3", "cs01:CS0102001A_S4", 1);	//  쪽지전송
		}
//trace(ds_mm031_3.SaveXML());
//return;		        
		tit_callService(
             ""
            , ""
			, "ds_mm031_0=ds_mm031_0:A ds_mm031_1 = ds_mm031_1:A ds_mm031_3=ds_mm031_3:A"
			, ""
			, ""
			, "fn_afterCancel"
			,""
			, ""
			, "");

	} else if (user_grp == "40"){  // 자재담당 승인취소

		for (var i = 0; i < ds_mm031_0.rowcount ; i++)
		{
			if (i > 0 && kind != ds_mm031_0.GetColumn(i,"RECE_F"))
			{
				gfn_showAlert("CW00167");   // 청구 취소와 승인 취소를 동시에 할수 없다.
				return;
			}


			if (ds_mm031_0.GetColumn(i,"RECE_F") == "1" || ds_mm031_0.GetColumn(i,"RECE_F") == "5")   // 전송취소처리
			{
				kind = ds_mm031_0.GetColumn(i,"RECE_F");

				ds_mm031_0.SetColumn(i,"GBN","E");
				if (!gfn_showConfirm("CI00013","전송취소")) {
					return;
				}
			}
			else if (ds_mm031_0.GetColumn(i,"RECE_F") == "2" || ds_mm031_0.GetColumn(i,"RECE_F") == "6") // 접수취소
			{
				kind = ds_mm031_0.GetColumn(i,"RECE_F");
				ds_mm031_0.SetColumn(i,"GBN","B");
				if (!gfn_showConfirm("CI00013","접수")) {
					return;
				}
			}
			else if (ds_mm031_0.GetColumn(i,"RECE_F") == "3") // 승인취소
			{
				kind = ds_mm031_0.GetColumn(i,"RECE_F");
				ds_list.fireevent = false;
				ds_list.GroupKey("REQNO,MATNR,BWTAR");
				if (ds_list.GetColumn(i,"STATE") != '1'){
					gfn_showAlert("CW00071", "승인취소(마감완료)");
					ds_list.GroupKey("REQNO,REQSN");
					ds_list.fireevent = true;
					return;
				}
				if (!gfn_showConfirm("CI00013","승인")) {
					return;
				}
				ds_mm031_0.SetColumn(i,"GBN","D");
			} 
			else
			{
				gfn_showAlert("CW00071", "작성/접수/승인 취소(상태확인)"); // 상태값이 없으면 오류
				return;
			}

			gfn_copyDs(ds_list, ds_list_0,"REQNO="+quote(ds_mm031_0.GetColumn(i,"REQNO")));
			ds_list.fireevent = true;
		}

		// MMPR 데이터셋을 복사
		gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2 || REQKIND=3 || REQKIND=4");

		//  승인취소
		gfn_copyDs(ds_list, ds_list_0, "REQKIND=2 || REQKIND=3 || REQKIND=4");

		tit_clearActionInfo();

       // 접수취소 ,승인취소
		tit_addMultiActionInfo("cs01:CS0102001A_U1");

		if (ds_mm031_1.rowcount < 1 ) {
			tit_addMultiActionInfo("cs01:CS0102001A_U2");
		}
		if (ds_mm031_1.rowcount > 0) {
			tit_addMultiActionInfo("cs01:CS0102001A_U3");
			tit_addMultiActionInfo("cs01:CS0102001A_U4");
			tit_addMultiActionInfo("cs01:CS0102001A_D6");
		}

		if (kind == "3")  // 승인취소
		{
			tit_addMultiActionInfo("cs01:CS0102001A_U5");
		}


		// 취소시 작성, 접수, 승인자에게 모두 쪽지 전송 : '14.02.06 hss 이동진 요청 - START

       if (ds_mm031_0.GetColumn(0,"GBN") == "B" || ds_mm031_0.GetColumn(0,"GBN") == "D")
       {
			gfn_copyDs(ds_mm031_0, ds_mm031_3, "CHECK=1");

			for(var i = 0; i < ds_mm031_0.rowcount; i++)
			{
				ds_mm031_3.addRow();
				ds_mm031_3.copyrow(ds_mm031_3.row,"ds_mm031_0", i);
				if (ds_mm031_3.GetColumn(i,"GBN") == "D")  // 승인취소
				{
					ds_mm031_3.SetColumn(ds_mm031_3.row, "ACNAM", ds_mm031_0.GetColumn(i,"CREID"));
				} else    // 접수취소
				{
					ds_mm031_3.SetColumn(ds_mm031_3.row, "CREID", ds_mm031_0.GetColumn(i,"ACNAM"));
				}
				if(length(ds_mm031_0.GetColumn(i,"APNAM")) > 0)
				{
					ds_mm031_3.addRow();
					ds_mm031_3.copyrow(ds_mm031_3.row,"ds_mm031_0", i);

					if (ds_mm031_3.GetColumn(i,"GBN") == "D")  // 승인취소
					{
						ds_mm031_3.SetColumn(ds_mm031_3.row, "ACNAM", ds_mm031_0.GetColumn(i,"APNAM"));
					} else   // 접수취소
					{
						ds_mm031_3.SetColumn(ds_mm031_3.row, "CREID", ds_mm031_0.GetColumn(i,"APNAM"));
					}
				}				
			}
		}

		// 취소시 작성, 접수, 승인자에게 모두 쪽지 전송 : '14.02.06 hss 이동진 요청 - END


		if (kind == "3" || kind == "2")  // 접수 승인 취소시
		{
			var mesgdesc = gfn_openPopup("CS0102001D", true);  // 취소 사유 쪽지 내용 등록

			if ( mesgdesc != null)
			{
				for(var i = 0; i < ds_mm031_3.rowcount; i++)
				{
					ds_mm031_3.SetColumn(i,"MESGDESC", mesgdesc);
				}
			}
			tit_addMultiActionInfo("cs01:CS0102001A_I3", "cs01:CS0102001A_S4", 1);	//  쪽지전송
		}
//trace(ds_mm031_3.SaveXML());
//return;
		tit_callService(
             ""
            , ""
			, "ds_mm031_0=ds_mm031_0:A ds_mm031_1=ds_mm031_1:A ds_list_0=ds_list_0:A ds_mm031_3=ds_mm031_3:A"
			, ""
			,""
			, "fn_afterCancel"
			,""
			, ""
			,"");

	}

}
