/********************************************************************************
* 기      능   : 전송 60
********************************************************************************/
function fn_trans() {

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	var user_grp = gfn_getUserGroup();


	// 청구전송 대상 자료 check
	var able = false;
	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		able = true;
		// if(ds_mm031_0.GetColumn(i, "RECE_F") != "0")
		// {
			// able = false;
		// }
		// FM 재고소진 청구 추가 13.08.13 HSS
		if(ds_mm031_0.GetColumn(i, "RECE_F") != "0" ||
		   ((( ds_mm031_0.GetColumn(i, "REQKIND") == "1" || ds_mm031_0.GetColumn(i, "REQKIND") == "6"
		   || ds_mm031_0.GetColumn(i, "REQKIND") == "7" || ds_mm031_0.GetColumn(i, "REQKIND") == "9" || ds_mm031_0.GetColumn(i, "REQKIND") == "H"
		   || ds_mm031_0.GetColumn(i, "REQKIND") == "A" || ds_mm031_0.GetColumn(i, "REQKIND") == "G") || 
		   ((ds_mm031_0.GetColumn(i, "REQKIND") == "2" || ds_mm031_0.GetColumn(i, "REQKIND") == "3" || ds_mm031_0.GetColumn(i, "REQKIND") == "4") 
		   && ds_mm031_0.GetColumn(i, "MMPR_DEL") == "X")) && length(ds_mm031_0.GetColumn(i, "QTNUM")) < 1))
		{
			able = false;
		}

		if (!able)
		{
			gfn_showAlert("CW00071", "전송(청구번호 : "+ quote(ds_mm031_0.GetColumn(i,"REQNO"))+")");
			return;
		}
	}

	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		ds_mm031_0.SetColumn(i,"GBN","A");
	}

	// MMPR 데이터셋을 복사
	gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2 || REQKIND=3 || REQKIND=4");

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs01:CS0102001A_U6");

	if (ds_mm031_1.rowcount < 1) {
		tit_addMultiActionInfo("cs01:CS0102001A_U7");
	}
	if (ds_mm031_1.rowcount > 0) {
		tit_addMultiActionInfo("cs01:CS0102001A_U8");
		tit_addMultiActionInfo("cs01:CS0102001A_U9");
	}

	tit_callService(
		 ""
		, ""
		, "ds_mm031_0=ds_mm031_0:A ds_mm031_1 = ds_mm031_1:A"
		, ""
		, ""
		, "fn_afterTrans"
        ,""
        , ""
		, "");
}
