function ufn_getGbn() {
	var gbn, pjt_val, cb1_val, cb2_val, cb3_val;
	pjt_val = ed_pjt.value;
	cb1_val = div_code.cb_big.value;
	cb2_val = div_code.cb_mid.value;
	cb3_val = div_code.cb_small.value;

	var user_grp = gfn_getUserGroup();

	if(user_grp == "10") { // 보수협력사 권한
		if(pjt_val == "") { // 조회조건을 입력하지 않을 경우
			gbn = 'A';
		} else { // 조회조건 prj를 입력할 경우
			gbn = 'B';
		}
	}
	if(user_grp == "20") { // 보수PM 권한
		if(pjt_val == "" && cb3_val == "") { // 조회조건을 입력하지 않거나 cb3을 전체로 입력할 경우
			gbn = 'C';
		} else if(pjt_val <> "") { // 조회조건 prj를 입력할 경우
			gbn = 'D';
		} else if(pjt_val == "" && cb3_val <> "") { // 조회조건 cb3을 입력할 경우
			gbn = 'E';
		} else if(pjt_val <> "" && cb3_val <> "") { // 조회조건 모두 입력할 경우
			gbn = 'F';
		}
	}
	if(user_grp == "30") { // 지방사무소 권한
		if(pjt_val == "" && cb3_val == "") { // 조회조건을 입력하지 않거나 cb3을 전체로 입력할 경우
			gbn = 'G';
		} else if(pjt_val <> "") { // 조회조건 prj를 입력할 경우
			gbn = 'H';
		} else if(pjt_val == "" && cb3_val <> "") { // 조회조건 cb3을 입력할 경우
			gbn = 'I';
		} else if(pjt_val <> "" && cb3_val <> "") { // 모두 입력할 경우
			gbn = 'J';
		}
	}
	if(user_grp == "40") { // 고객지원부 권한
		if(pjt_val <> "") { // 조회조건 prj를 입력할 경우
			gbn = 'K';
		} 
		else if(pjt_val == "" && cb1_val == "1" && cb2_val <> "" && cb3_val == "") { // prj를 입력하지 않고 cb1:지역,cb2:지역,cb3:전체 로 입력할 경우
			gbn = 'L';
		} 
		else if(pjt_val == "" && cb1_val == "1" && cb2_val <> "" && cb3_val <> "") { // prj를 입력하지 않고 cb1:지역,cb2:지역,cb3:협력사 로 입력할 경우
			gbn = 'M';
		} 
		else if(pjt_val == "" && cb1_val == "2" && cb2_val <> "" && cb3_val == "") { // prj를 입력하지 않고 cb1:PM,cb2:PM,cb3:전체 로 입력할 경우
			gbn = 'N';
		} 
		else if(pjt_val == "" && cb1_val == "2" && cb2_val <> "" && cb3_val <> "") { // prj를 입력하지 않고 cb1:PM,cb2:PM,cb3:협력사 로 입력할 경우
			gbn = 'M';
		} 
		else if(pjt_val == "" && cb1_val == "3" && cb2_val <> "" && cb3_val == "") { // prj를 입력하지 않고 cb1:팀,cb2:팀,cb3:전체 로 입력할 경우
			gbn = 'O';
		} 
		else if(pjt_val == "" && cb1_val == "3" && cb2_val <> "" && cb3_val <> "") { // prj를 입력하지 않고 cb1:팀,cb2:팀,cb3:협력사 로 입력할 경우
			gbn = 'M';
		} 
		else if(pjt_val == "" && cb1_val == "4" && cb2_val <> "" && cb3_val == "") { // prj를 입력하지 않고 cb1:부서,cb2:부서,cb3:전체 로 입력할 경우
			gbn = 'P';
		}
		 else if(pjt_val == "" && cb1_val == "4" && cb2_val <> "" && cb3_val <> "") { // prj를 입력하지 않고 cb1:부서,cb2:부서,cb3:부서 로 입력할 경우
			gbn = 'Q';
		} 

	}

	return gbn;
}

