/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	ds_mm031.setColumn(ds_mm031.row, "BILDAT2", strText);

	obj.Visible = false;
