function ds_mm031_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "BILDAT2" ) {
		ds_list.fireevent = false;

		if(Trim(obj.GetColumn(nRow,"BILDAT2")) != Trim(ds_mm031_2.GetColumn(nRow,"BILDAT2")))
		{
			obj.SetColumn(nRow,"CHECK","1");
		}
		else
		{
			obj.SetColumn(nRow,"CHECK","0");
		}
		ds_list.fireevent = true;
	}	
}

function ufn_PrintMMPR(obj)
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var chksize = 0;
	var nRow = 0;

	ds_list.UnFilter();
	ds_list.fireevent = false;
	for (var i = 0; i < ds_mm031.rowcount ; i++){
		if (ds_mm031.GetColumn(i,"CHECK") == "1" && length(ds_mm031.GetColumn(i,"JQPRNUM")) > 0){
			for (var j = 0; j < ds_list.rowcount; j++)
			{
				if (ds_mm031.GetColumn(i,"JQPRNUM") == ds_list.GetColumn(j,"JQPRNUM"))
				{
					ds_list.SetColumn(j,"CHECK","1");
				}
			}
			nRow = i;
			chksize++;
		}
	}
	ds_list.fireevent = true;

	if (chksize == 0){
		gfn_showMsg("CW00065", "출력할 MMPR대상을");
		return;
	}

	ds_list_1.ClearData();
	//ds_list.fireevent = false;
	gfn_copyDs(ds_list, ds_list_1, "CHECK=1");
	ds_list_1.Sort("LGORT,JQPRNUM,MATNR");
	//ds_list.fireevent = true;

	ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(nRow, "REQNO")));


	if (ds_list_1.rowcount > 0){
		Dialog("CS01::CS0102001B.xml","", -1, -1, "Resize=true");	
	}
}

function grd_sum_OnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	gfn_clearMsgBar();
	var dsObj = object(obj.BindDataset);
	var user_grp = gfn_getUserGroup();
	var arg  = " fa_pjt="    + quote(dsObj.GetColumn(nRow,"PS_PSPID"));
	    arg += " fa_qtnum="  + quote(dsObj.GetColumn(nRow,"QTNUM"));
	    arg += " fa_lgort1=" + quote(dsObj.GetColumn(nRow,"LGORT1"));
	    arg += " fa_reqnr="  + quote(dsObj.GetColumn(nRow,"REQNR"));
	    arg += " fa_reqno="  + quote(dsObj.GetColumn(nRow,"REQNO"));
	    arg += " fa_vkgrp="  + quote(dsObj.GetColumn(nRow,"VKGRP"));


	// FM청구
	if(user_grp >= '10' && (dsObj.GetColumn(nRow,"REQKIND") == "9" || dsObj.GetColumn(nRow,"REQKIND") == "H" ))
	{
		//gfn_openPopup("CS0102001C", true, "fa_pjt=" + quote(dsObj.GetColumn(nRow,"PS_PSPID")), 610, 568);
		// CS0102001C
		gfn_openPopup("CS0102001C", true, arg, 610, 568);
	}

	// MMPR 조회 및 출력
	if( user_grp >= '10' && ( dsObj.GetColumn(nRow,"REQKIND") == "2" || dsObj.GetColumn(nRow,"REQKIND") == "3" || dsObj.GetColumn(nRow,"REQKIND") == "4" ) 	)
	{
		ufn_ViewMMPROpen(obj);
	}

	// 무상청구 조회 및 출력
	if( user_grp >= '10' && ( dsObj.GetColumn(nRow,"REQKIND") == "A" )  	)
	{
		if( parseInt(dsObj.GetColumn(nRow,"CNT126")) == 0 ) {
			gfn_showMsg("유상계약정보가 존재하지 않습니다. 결재선을 지정할 수 없습니다. 계약정보를 확인하세요!");
			return;
		} 
		gfn_openPopup("CS0101010B", true, arg, 610, 568);
	}
}
/* MMPR 더블클릭시 상세화면 */
function ufn_ViewMMPROpen(obj)
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var nRow = ds_mm031.currow;
	if (length(ds_mm031.GetColumn(nRow,"JQPRNUM")) > 0){
		gfn_openPopup("CS0101001B", true, "fa_jqprnum=" + quote(ds_mm031.GetColumn(nRow,"JQPRNUM")), 610, 523);
	}
	else
	{
		gfn_showMsg("CW00065", "하나의 조회할 MMPR대상을");
		return;
	}
}

