var posid_yn 	= false; // 호기번호 입력여부
var reqno_yn 	= false; // 문서번호 입력여부
var reqnr_yn 	= false; // 청구번호 입력여부
var upn_yn   	= false; // 통합 Proj 입력여부
var qtnum_yn 	= false; // 견적번호 입력여부
var matnr_yn 	= false; // 자재번호 입력여부
var ps_pspid_yn = false; // pjt.번호  입력여부

//var original_reqfmdate = ''; // 기존 청구시작일자
//var original_reqtodate = ''; // 기존 청구종료일자
var vBtn_auth = false; // 버튼실행권한
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

    // 청구종류코드,청구진행,사업장,자재평가유형코드  DataSet 가져오기
    var codeArr = ["3","7","CS151","2","AP_GUBN"];
    var dsObjArr = [ds_reqkind,ds_rece_f,ds_bussi,ds_bwtar,ds_apgubn];
    var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_ALL,G_CODE_NONE,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    cal_reqdate_to.value = gfn_getCurrDate();
    cal_reqdate_fm.value = gfn_getBeforeDate(cal_reqdate_to.value, 7);

   // cal_apdat_to.value = gfn_getCurrDate();
   // cal_apdat_fm.value = gfn_getBeforeDate(cal_apdate_to.value, 7);

    btn_serno_into.Enable = true;
    btn_serno_del.Enable = true;

    var user_grp = gfn_getUserGroup();

	if(user_grp <> "40"){
		btn_serno_del.Enable = false;
		return;
	}
	// 특정사용자 임시 적용(요청에 의한 추가) 2018.07.16 ch 
	//if(g_user_id == "2023709" || g_user_id == "3018318" || g_user_id == "3011038" || g_user_id == "2020882" || g_user_id == "2018209" || g_user_id == "3024504" || g_user_id == "2024233" || g_user_id == "2028182" || g_user_id == "3002519" || g_user_id == "3005160")
	//{
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS0102001A");
	ds_rcond.SetColumn(0, "GBN", "02");	
	vBtn_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	if (vBtn_auth == true)
	{
		btn_return.Enable = true;
		btn_cancelreturn.Enable = true; 
		btn_returnprint.Enable = true;
	} else {
		btn_return.Enable = false;
		btn_cancelreturn.Enable = false; 
		btn_returnprint.Enable = false;
	}

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_rece_f.Enable = true;
	cb_reqkind.index = 0;
	cb_rece_f.index = 0;
	cb_wbap_pst.index = 0;
	cb_bussi.index = 0;
	ds_wbap_pst.filter("CODE <> '99'");
	gfn_showMsg("CI00007");
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
