/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	// 최대 12개월로 제한. 2019.04.04 Han J.H
	var diffDays = gfn_getDayCnt(ds_days, cal_reqdate_fm.value, cal_reqdate_to.value);

	// 2019.04.05 필수값 체크
	searchValueChk();

	var user_grp = gfn_getUserGroup();	

	// 필수조회 조건중 한가지 항목이라도 입력되었을 경우, 조회기간을 다음과 같이 강제로 세팅함.
	if(posid_yn == true || reqno_yn == true || reqnr_yn == true || upn_yn == true || qtnum_yn == true || ps_pspid_yn == true || matnr_yn == true) {
		//ds_cond.SetColumn(0,"REQDATE_FM", "20000101");
		//ds_cond.SetColumn(0,"REQDATE_TO", gfn_getCurrDate());
	} else {

		// 40권한이면서 보수협력사 공통콤보의 중간값이 전체일 경우 조회기간을 3개월로 제한함.
		if(user_grp == '40' && gfn_isNull(div_code.cb_mid, true, Static7.value)) {
			if(diffDays > 90) {
				gfn_showAlert("CW00078","3");
				cal_reqdate_fm.SetFocus();
				return;				
			}
		}

		// 필수조건이 입력되지 않았을 경우에는 조회기간을 1년으로 제한함.
		if(diffDays > 365) {
			gfn_showAlert("CW00078","12");
			cal_reqdate_fm.SetFocus();
			return;
		}		
	}

	if(matnr_yn == true) {
		// 자재번호의 경우 최소 6자리 이상으로 체크
		if(length(trim(ds_cond.GetColumn(0,"MATNR"))) < 6) {
			gfn_showAlert("자재번호의 경우 최소 6자리 이상으로 입력해주세요.");
			matnr_yn = false;
			ed_matnr.SetFocus();
			return;
		}

		// 자재번호 조회 시 자릿 수 최대 9자리로 제한
		if(length(trim(ds_cond.GetColumn(0,"MATNR"))) > 9) {
			gfn_showAlert("자재번호의 최대 검색 자릿수는 9자리 입니다.");
			matnr_yn = false;
			ed_matnr.SetFocus();
			return;			
		}
		var str = replace(trim(ds_cond.GetColumn(0,"MATNR")), "%", "");
		// 한글입력 제한
		for( var i = 0; i < length( str); i++ ) {
			if ( lengthb(charAt( str, i)) == 2 ) {
				gfn_showAlert("자재번호로 검색시에는 한글입력이 제한됩니다.");
				matnr_yn = false;
				ed_matnr.SetFocus();			
				return;
			}
		}		
	}

	if(ps_pspid_yn == true) {
		if(length(trim(ds_cond.GetColumn(0,"PS_PSPID"))) < 5) {
			gfn_showAlert("PJT번호의 경우 최소 5자리 이상으로 입력해주세요.");
			ps_pspid_yn = false;
			ed_pspid.SetFocus();
			return;
		}	
	}	

	// 40 권한의 경우 지역, 보수PM, 부서, 팀 中 한가지라도 선택하도록 제약
	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, Static7.value)) {
			var arr = [Static7.value];
			gfn_showMsg("CW00001", arr);
			return;
		}
	}	

	/*if(diffDays > 365) {
		gfn_showAlert("CW00078","12");
		cal_reqdate_fm.SetFocus();
		return;
	}*/

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	if(!gfn_checkDate(cal_reqdate_fm.value, cal_reqdate_to.value, true, cal_reqdate_fm)) {
		return;
	}

	if(length(trim(ed_upn.Text)) > 0 && length(trim(ed_upn.Text)) < 6) {
		gfn_showAlert("통합Proj는 최소 6자리를 입력하셔야 합니다.");
		return;
	}

	//조회기간: 90일 이상 조회 불가 -- 20110113 조요한 과장 요청으로 늘림
	// if( (gfn_isNull(ed_pspid, false) && gfn_isNull(ed_pjt, false)) 
	    // && gfn_getDayCnt(ds_days,cal_reqdate_fm.value, cal_reqdate_to.value) > 90)
	// {
		// gfn_showAlert("CW00078","3");
		// cal_reqdate_fm.SetFocus();
		// return;
	// }

	//보수콤보 적용 
	ufn_getGbn();

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs01:CS0102001A_S1");
	tit_addSearchActionInfo("cs01:CS0102001A_S2");
	//tit_addSearchActionInfo("cs01:CS0102001A_S7");  //grd_exlist2 출력용 dataset추가 17.02 yar

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_mm031=ds_mm031 ds_list=ds_list ds_exlist2=ds_exlist2"
        , ""
        , "fn_afterQuery"
        , true);
}
