/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	if (ds_mm031.rowcount() < 1) return;

	// Excel Export 
	gfn_exportExcel(grd_sum, true );

	// 하부 그리드 출력
	if (gfn_getUserGroup() > "30")
	{
		ds_list.UnFilter();

		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(ds_mm031.currow,"REQNO")));

		grd_list.ExportExcel("grd_list",false, false );
	}
}
/********************************************************************************
* 기      능   : 접수 5
********************************************************************************/
function fn_receipt() {

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	// 처리 데이터 갯수제한(10건 초과시 알림창 표출)
	// 2019.01.15 Han J.H
	if (ds_mm031_0.rowcount > 10) {
		gfn_showAlert("접수처리의 경우 시스템 속도제약으로 인하여, 10건 이하의 건만 처리 가능합니다.");
		return;
	}

	var user_grp = gfn_getUserGroup();

	// 보수 PM 이나 고객지원만 접수 승인가능
	if (user_grp != "20" && user_grp != "30" &&  user_grp != "40")
	{
		gfn_showAlert("CW00071", "접수");
		return;
	}

	// 청구접수 대상 자료 check
	var able = false;
	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{

		able = true;
		if((ds_mm031_0.GetColumn(i, "RECE_F") != "1"  && ds_mm031_0.GetColumn(i, "RECE_F") != "5")||
		   ((( ds_mm031_0.GetColumn(i, "REQKIND") == "1" 
		    || ds_mm031_0.GetColumn(i, "REQKIND") == "6"
		    || ds_mm031_0.GetColumn(i, "REQKIND") == "7" 
		    || ds_mm031_0.GetColumn(i, "REQKIND") == "G"
		    || ds_mm031_0.GetColumn(i, "REQKIND") == 'S') || 
		   ((ds_mm031_0.GetColumn(i, "REQKIND") == "2" 
		   || ds_mm031_0.GetColumn(i, "REQKIND") == "3" 
		   || ds_mm031_0.GetColumn(i, "REQKIND") == "4") 
		   && ds_mm031_0.GetColumn(i, "MMPR_DEL") == "X")) 
		   && length(ds_mm031_0.GetColumn(i, "QTNUM")) < 1))
		{
			able = false;
		}
		if (!able)
		{
			gfn_showAlert("CW00071", "접수(청구번호 : "+ quote(ds_mm031_0.GetColumn(i,"REQNO"))+")");
			return;
		}
	}

	if (!gfn_showConfirm("CI00014","접수")) {
		return;
	}

	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		ds_mm031_0.SetColumn(i,"GBN","A");
	}

	// MMPR 데이터셋을 복사
	gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2 || REQKIND=3 || REQKIND=4");

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs01:CS0102001A_U1");

	//==============================MMPR과 유상청구 접수 시 견적헤더 상태값이 변경되지 않으므로 로직변경=======================
	/*if (ds_mm031_1.rowcount < 1 ) {
		tit_addMultiActionInfo("cs01:CS0102001A_U2");
	}*/
	//=========================================================================================================================
	if (ds_mm031_1.rowcount > 0) {
		tit_addMultiActionInfo("cs01:CS0102001A_U3");
		tit_addMultiActionInfo("cs01:CS0102001A_U4");
	}

	// 2019.07.04 처리순서 변경. Han J.H
	tit_addMultiActionInfo("cs01:CS0102001A_U2");

	tit_callService(
		 ""
		, ""
		, "ds_mm031_0=ds_mm031_0:A ds_mm031_1 = ds_mm031_1:A"
		, ""
		, ""
		, "fn_afterReceipt"
		, "");

}
/********************************************************************************
* 기      능   : 리드 타임 변경 청구 건 확인 
** 2	MMPR 청구
** 5	업체 STOCK 청구
** 9	FM청구
** A	무상청구
** D	HRTS청구
** F	비상통화장치청구
********************************************************************************/
function fn_checkChangeLeadtime(reqkind) {
	trace("reqkind == " + reqkind);
	if(reqkind == "2" || reqkind == "5" || reqkind == "9" 
	                  || reqkind == "A" || reqkind == "D" || reqkind == "F" || reqkind == "I" ) 
	{
		return true;
	} else {
		return false;
	}
}
/********************************************************************************
* 기      능   : 승인 10
********************************************************************************/
function fn_approval() {
	// 리드타임 승인일 기준 재계산 201712 이하림 
	var max_webaz = 0;
	//var checkChangeLeadtime = false;
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");

	for( var i=0; i < ds_mm031_0.rowcount; i++ )
	{	// 승인일 기준 리드타임 기준 납기일 변경 201712 이하림
		//max_webaz = parseInt(ds_mm031_0.GetColumn(i, "WEBAZ")) + 1;

		// 리드타임 기준 납기일 변경. 2021.06.23 서비스자재팀 김태한 팀원 요청. HJH
		max_webaz = parseInt(ds_mm031_0.GetColumn(i, "WEBAZ")) + 4;

		//var change_rdate = gfn_getAfterDate(gfn_getCurrDate(), parseInt(max_webaz));

		var change_rdate = gfn_getWorkingDate(max_webaz, '2000', gfn_getCurrDate()); // 2021.07.06 캘린더데이 => 워킹데이로 기준 변경. 서비스자재팀 김태한 요청. Han JH

		//checkChangeLeadtime = fn_checkChangeLeadtime(ds_mm031_0.GetColumn(i, "REQKIND"));

		/*if(checkChangeLeadtime) {
			ds_mm031_0.SetColumn(i,"RDATE",change_rdate);
		}*/

		ds_mm031_0.SetColumn(i,"RDATE",change_rdate); // 청구종류 상관없이 출고요청일 변경되도록 처리. 2021.06.23 서비스자재팀 김태한 팀원 요청. HJH

		for( var j=0; j < ds_list.rowcount; j++ )
		{
			if(ds_mm031_0.GetColumn(i,"REQNO") == ds_list.GetColumn(j,"REQNO")) 
			{
				ds_list.SetColumn(j,"CHECK","1");
			} else {
				ds_list.SetColumn(j,"CHECK","");
			}
		}
	}

	gfn_copyDs(ds_list, ds_list_5, "CHECK=1");

	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	var user_grp = gfn_getUserGroup();

	// 고객지원만 접수 승인가능
	// if (user_grp != "40")
	// {
		// gfn_showAlert("CW00071", "승인");
		// return;
	// }

    // 승강기 비승강기에 따라 승인 대상 구분 20123.09.12 HSS  -> 분류데이터관리 
	var arr = split(G_ORG_USER_INFO, "^", true);
	var apgubn = ds_apgubn.GetColumn(ds_apgubn.searchRow("CODE== " + quote(arr[0])),"CODE_NAME");

	if(length(apgubn) < 0)
	{
		alert("1");
		gfn_showAlert("CW00071", "승인");
		return;
	}

	if (user_grp < 30)
	{
		gfn_showAlert("CW00071", "승인");
		return;
	}

	// 청구접수 대상 자료 check
	var able = false;
	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		able = true;

		// 승강기 비승강기에 따라 승인 대상 구분 추가 20123.09.12 HSS
		// -> 공통구분 추가 (승강기 비승강기 모두 승인) 2013.09.30 hss
		if(
			(ds_mm031_0.GetColumn(i, "RECE_F") != "2" && ds_mm031_0.GetColumn(i, "RECE_F") != "6") 
			|| (
					(( ds_mm031_0.GetColumn(i, "REQKIND") == "1" 
					|| (ds_mm031_0.GetColumn(i, "REQKIND") == "6" && length(ds_mm031_0.GetColumn(i, "POSID")) < 1) //WBS번호 추가
					|| (ds_mm031_0.GetColumn(i, "REQKIND") == "G" && length(ds_mm031_0.GetColumn(i, "POSID")) < 1) //WBS번호 추가
					|| ds_mm031_0.GetColumn(i, "REQKIND") == "7"
					|| ds_mm031_0.GetColumn(i, "REQKIND") == "S") 
					|| ((ds_mm031_0.GetColumn(i, "REQKIND") == "2" || ds_mm031_0.GetColumn(i, "REQKIND") == "3" || ds_mm031_0.GetColumn(i, "REQKIND") == "4") 
		   && ds_mm031_0.GetColumn(i, "MMPR_DEL") == "X")) && length(ds_mm031_0.GetColumn(i, "QTNUM")) < 1)
		   || ( apgubn != "3" && ((ds_mm031_0.GetColumn(i, "BUSSI") == "1" && apgubn != "1") || (ds_mm031_0.GetColumn(i, "BUSSI") != "1" && apgubn == "1"))))  
		{
			able = false;
		}
		if (!able)
		{
			gfn_showAlert("CW00071", "승인(청구번호 : "+ quote(ds_mm031_0.GetColumn(i,"REQNO"))+")");
			return;
		}
	}

	if (!gfn_showConfirm("CI00014","승인")) {
		return;
	}
	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		ds_mm031_0.SetColumn(i,"GBN","C");
	}

	// MMPR 데이터셋을 복사
	gfn_copyDs(ds_mm031_0, ds_mm031_1, "REQKIND=2  || REQKIND=3 || REQKIND=4");


	//==============================MMPR과 유상청구 승인 시 견적헤더 상태값이 변경되지 않으므로 로직변경=======================
	/*if (ds_mm031_1.rowcount < 1 ) {
		tit_addMultiActionInfo("cs01:CS0102001A_U2");
	}*/
	//=========================================================================================================================

//=============>MMPR ZQMT007-REQNR 간혹 누락으로 인한 수정  [시작]<==================
	/*
	tit_clearActionInfo();
	if (ds_mm031_1.rowcount > 0) {
		tit_addMultiActionInfo("cs01:CS0102001A_U3");
		tit_addMultiActionInfo("cs01:CS0102001A_U4");
		tit_addMultiActionInfo("cs01:CS0102001A_I1");
	}
	// 2019.07.04 처리순서 변경. Han J.H
	tit_addMultiActionInfo("cs01:CS0102001A_U2");

	// ZMMT031-RDATE 변경 201712 이하림 
	tit_addMultiActionInfo("cs01:CS0102001A_U14");

	tit_addMultiActionInfo("cs01:CS0102001A_I2");

	tit_callService(
		 ""
		, ""
		, "ds_mm031_0=ds_mm031_0:A ds_mm031_1 = ds_mm031_1:A ds_list_5 = ds_list_5:A"
		, ""
		, ""
		, "fn_afterApproval"
		, "");

	*/

	if (ds_mm031_1.rowcount > 0) {
		tit_clearActionInfo();
		tit_addMultiActionInfo("cs01:CS0102001A_U3");
		tit_addMultiActionInfo("cs01:CS0102001A_U4");
		tit_addMultiActionInfo("cs01:CS0102001A_I1");		
		tit_callService(
			""
			, ""
			, "ds_mm031_1 = ds_mm031_1:A ds_list_5 = ds_list_5:A"
			, ""
			, ""
			, "fn_after1"
			, ""
			, ""
			, "true");					
	}
	else {
		fn_after1();
	}



/*	
	tit_clearActionInfo();
	// 2019.07.04 처리순서 변경. Han J.H
	tit_addMultiActionInfo("cs01:CS0102001A_U2");	
	// ZMMT031-RDATE 변경 201712 이하림 
	tit_addMultiActionInfo("cs01:CS0102001A_U14");	
	tit_addMultiActionInfo("cs01:CS0102001A_I2"); // db2 프로시져 실행
	tit_callService(
