// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 디테일 정보 필터링
	if (ds_mm031.rowcount > 0)
	{
		gfn_copyDs(ds_mm031, ds_mm031_2,);
		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(0,"REQNO")));
	} else {
		// 헤더와 디테일의 쿼리가 상이한 문제로 인하여, 헤더의 정보가 없을시 디테일의 정보는 보여주지 않음으로 처리
		// 2019.04.08 Han J.H
		ds_list.ClearData();
	}

	if (ds_list.rowcount > 0)
	{
		gfn_copyDs(ds_list, ds_list_4);
	}

	// 필수입력조건 체크내역 해제
	posid_yn = false;
	reqno_yn = false;
	reqnr_yn = false;
	upn_yn = false;
	qtnum_yn = false;
	matnr_yn = false;
	ps_pspid_yn = false; // 2019.05.09 Han J.H

	// 필수입력조건 으로 조회시 조회기간 제한없이 검색하기 위해 변경했던 부분을 기존 화면의 날짜로 원복
	//cal_reqdate_fm.Value = original_reqfmdate;
	//cal_reqdate_to.Value = original_reqtodate;

// 메시지 처리 
//	gfn_showSearchMsg(grd_list);
	gfn_showMsg("CI00002",ds_mm031.RowCount()+"");
}
