/********************************************************************************
* 기      능   : 부품요청서 출력 / 이종석/ 20150129
********************************************************************************/
function btn_print_req_OnClick(obj)
{
	//요기서 세팅을 해야한다.
	var currentRow = grd_sum.currentRow;
	if(currentRow < 0) return;

	if(!(ds_mm031.GetColumn(currentRow, "REQKIND") == "9" ||
		 ds_mm031.GetColumn(currentRow, "REQKIND") == "H"))
	{
		return;
	}


	ds_report_cond.clearData();
	ds_report_cond.AddRow();

	ds_report_cond.SetColumn(0, "REQNO", ds_mm031.GetColumn(currentRow, "REQNO"));
	ds_report_cond.SetColumn(0, "LGORT", ds_mm031.GetColumn(currentRow, "LGORT"));
	ds_report_cond.SetColumn(0, "UPN", substr(ds_mm031.GetColumn(currentRow, "UPN"), 0, 6));
	ds_report_cond.SetColumn(0, "CST", substr(ds_mm031.GetColumn(currentRow, "UPN"), 6));

	tit_clearActionInfo();					
	tit_addSearchActionInfo("cs01:CS0102001A_S5");								//부품요청서 작성용 부품리스트 가져오기
	tit_addSearchActionInfo("cs01:CS0102001A_S6");								//부품요청서 작성용 상세정보 가져오기
	tit_callService("", "", "ds_report_cond=ds_report_cond", "ds_list_parts=ds_list_parts ds_pjt_det=ds_pjt_det", "", "", true, false, true);	

	gfn_copyDs(ds_pjt_det, ds_vkdet);

	if(ds_vkdet.rowcount == 0)
	{
		ds_vkdet.AddRow();
	}

	if(gfn_showConfirm("CI30001", "부가세 포함으로 견적하시겠습니까?                   확인:부가세포함, 취소:부가세미포함"))
	{
		ds_vkdet.SetColumn(0, "VAT", "1");
	}
	else
	{
		ds_vkdet.SetColumn(0, "VAT", "");
	}

	Dialog("CS01::CS0101008G.xml", "");
}

function btn_wf_stat_OnClick(obj) {
	var docNo;

	switch (grd_sum.CurrentCell)
	{
		case 16:
			docNo = ds_mm031.GetColumn(grd_sum.CurrentRow, "DOCNO");
			break;
		default:
			break;
	}

	DEBUG("btn_wf_stat_OnClick", "currentRow is " + grd_sum.CurrentRow + ", currentCell is " + grd_sum.CurrentCell + ", docNo is " + docNo);

	// 견적작성한 건들도 결재내역 확인 필요로 주석처리 16.12.07 YAR
	// if(substr(docNo, 0, 2) != "30" && substr(docNo, 0, 2) != "50")
	// {
		// return;
	// }

	ds_wf_cond.SetColumn(0, "DOCNO", docNo);

	tit_clearActionInfo();														
	tit_addSingleActionInfo("cs01:CS0101008A_S3");								//결재상세정보를 가져온다.
	tit_callService("", "", "ds_cond=ds_wf_cond", "ds_wf_stat=ds_wf_stat", "", "", true, false,true);

	if(ds_wf_stat.rowcount <= 0)
	{
		return;
	}

	var statStr = "";

	for(var iCnt = ds_wf_stat.rowcount - 1; iCnt >= 0; iCnt--)
	{
		statStr += ds_wf_stat.GetColumn(iCnt, "APPGUBNNM") + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "SEQNONM")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "SEQSTATNM")  + "\n    " + 
				   ds_wf_stat.GetColumn(iCnt, "APDPT1_N")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "APRNK")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "APIRUM")  + "\n";				   	
	}

	DEBUG("btn_wf_stat_OnClick", "ds_wf_stat is " + ds_wf_stat.SaveXml());

	gfn_openMsgWin(statStr, "", "A", docNo + " 결재상태");
}
function btn_returnprint_OnClick(obj)
{
// 반송증 출력 추가 2018.01.07 조영욱

// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var chksize = 0;
	var nRow = 0;
	var apdesc; 

	ds_list.UnFilter();
	ds_list.fireevent = false;
	for (var i = 0; i < ds_mm031.rowcount ; i++){

		if (ds_mm031.GetColumn(i,"CHECK") == "1" && length(ds_mm031.GetColumn(i,"RET_DAT")) > 0){
			for (var j = 0; j < ds_list.rowcount; j++)
			{
				if (ds_mm031.GetColumn(i,"REQNO") == ds_list.GetColumn(j,"REQNO"))
				{
					ds_list.SetColumn(j,"CHECK","1");
				}	
			}
			nRow = i;
			chksize++;
		}
	}
	ds_list.fireevent = true;

	if (chksize == 0){

		gfn_showMsg("CW00065", "출력할 MMPR대상을");
		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(nRow, "REQNO")));
		return;
	}
	ds_list_6.ClearData();
	//ds_list.fireevent = false;
	gfn_copyDs(ds_list, ds_list_6, "CHECK=1");

	for (var i = 0; i < ds_list_6.GetRowCount(); i++)
	{
		if(ds_list_6.GetColumn(i,"RET_DAT") == "")
		{
			gfn_showAlert("CW00001","자재반송일");
			ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(i, "REQNO")));
			return;
		}
	}

	ds_list_6.Sort("LGORT,JQPRNUM,MATNR");
	//ds_list.fireevent = true;

	ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(nRow, "REQNO")));


	if (ds_list_6.rowcount > 0){
		Dialog("CS01::CS0102001F.xml","", -1, -1, "Resize=true");	
	}

}

function btn_return_OnClick(obj)
{
// 반송증 출력 추가 2018.01.07 조영욱

// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	ds_mm031_5.ClearData();
	gfn_copyDs(ds_mm031, ds_mm031_5, "CHECK=1");

	if (ds_mm031_5.GetRowCount() < 1)
	{
		gfn_showAlert("CW00180","체크표시");
		return;
	}

	for (var i=0; i < ds_mm031_5.GetRowCount(); i++)
	{
		if(ds_mm031_5.GetColumn(i,"RECE_F") != "1" && ds_mm031_5.GetColumn(i,"RECE_F") != "2")
		{
			gfn_showAlert("CW00017");
			return;	
		}
	}

	var ret = gfn_openPopup("CS0102001G", true);

	for (var i=0; i < ds_mm031_5.GetRowCount(); i++)
	{
		if( ret == 1) {
			ds_mm031_5.SetColumn(i,"BIGO","MMPR대상 아님");
		} else if (ret == 2) {
			ds_mm031_5.SetColumn(i,"BIGO","품명 및 SPEC오류");
		} else if (ret == 3) {
			ds_mm031_5.SetColumn(i,"BIGO","실물 및 수량 오류");
		}else if (ret == "" || ret == null) {
			return;
		} else {
			ds_mm031_5.SetColumn(i,"BIGO",ret);
		}
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo(
		"cs01:CS0102001A_S8"
	   ,"cs01:CS0102001A_I5"
	   ,"cs01:CS0102001A_U15");

	//저장 처리 
	tit_callService(
		""
		, ""
		, "ds_mm031_5=ds_mm031_5:A"
		, ""
		, ""
		, "fn_afterSave");		

}

function btn_cancelreturn_OnClick(obj)
{

// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	ds_mm031_6.ClearData();
	gfn_copyDs(ds_mm031, ds_mm031_6, "CHECK=1");

	if (ds_mm031_6.GetRowCount() < 1)
	{
		gfn_showAlert("CW00180","체크표시");
		return;
	}

	for (var i=0; i < ds_mm031_6.GetRowCount(); i++)
	{
		if(length(ds_mm031_6.GetColumn(i,"RET_DAT")) < 1)
		{
			gfn_showAlert("CW00017");
			return;	
		}
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs01:CS0102001A_D7");

		tit_callService(
             ""
            , ""
			, "ds_mm031_6=ds_mm031_6:A"
			, ""
			, ""
			, "fn_afterDelete"
			, "");
}

// 필수조건 체크
function searchValueChk() {
	if(length(trim(ds_cond.GetColumn(0,"POSID")))) { // 호기번호가 Null값이 아니면
		posid_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"REQNO")))) { // 문서번호가 Null값이 아니면
		reqno_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"REQNR")))) { // 청구번호가 Null값이 아니면
		reqnr_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"UPN")))) { // 통합Proj가 Null값이 아니면
		upn_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"QTNUM")))) { // 견적번호가 Null값이 아니면
		qtnum_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"MATNR")))) { // 자재번호가 Null값이 아니면
		matnr_yn = true;
	}	

	if(length(trim(ds_cond.GetColumn(0,"PS_PSPID")))) { // pjt 번호가 Null값이 아니면
		ps_pspid_yn = true;
	}		



}
