var calendar_auth = true;

var posid_yn = false; // 호기번호 입력여부
var reqno_yn = false; // 문서번호 입력여부
var jqprnum_yn = false; // MMPR.번호 입력여부
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

    // 청구종류코드,청구진행,사업장,자재평가유형코드  DataSet 가져오기
    var codeArr = ["3","7","CS151","2","AP_GUBN"];
    var dsObjArr = [ds_reqkind,ds_rece_f,ds_bussi,ds_bwtar,ds_apgubn];
    var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_ALL,G_CODE_NONE,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // SE팀의 경우 수신결제기간 및 승인기간을 세팅함
    // 품질보증팀의 경우 승인기간 및 업무구분지정기간을 세팅함

	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS0102001H");
	ds_rcond.SetColumn(0, "GBN", "01");	
	calendar_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if(calendar_auth == true) { // 품질보증팀
		// 승인기간
		cal_apdat_to.value = gfn_getCurrDate();
		cal_apdat_fm.value = gfn_getBeforeDate(cal_apdat_to.value, 1);					

		// 업무구분지정기간
		cal_apdat016_to.value = gfn_getCurrDate();
		cal_apdat016_fm.value = gfn_getBeforeDate(cal_apdat016_to.value, 1);							

	} else { // SE팀
		// 수신결제기간
		cal_redat_to.value = gfn_getCurrDate();
		cal_redat_fm.value = gfn_getBeforeDate(cal_redat_to.value, 1);	

		// 승인기간
		cal_apdat_to.value = gfn_getCurrDate();
		cal_apdat_fm.value = gfn_getBeforeDate(cal_apdat_to.value, 1);			
	}

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_rece_f.Enable = true;
	cb_reqkind.index = 0;
	cb_rece_f.index = 0;
	cb_wbap_pst.index = 0;
	cb_bussi.index = 0;
	ds_wbap_pst.filter("CODE <> '99'");

	// 2018.08.17 Han J.H
    // MMPR청구, MMPR수리청구, MMPR 업체재고소진의 경우만 필터링 처리
    ds_reqkind.filter("CODE_NAME == '- 전체 -' || CODE_NAME == 'MMPR 청구' || CODE_NAME == 'MMPR 수리청구' || CODE_NAME == 'MMPR 업체재고 소진'");
	gfn_showMsg("CI00007");
}
