/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_cond.SetColumn(0, "JOBKIND", cb_jobkind.Value);

	var user_grp = gfn_getUserGroup();

	searchValueChk();

	// 40 권한의 경우 지역, 보수PM, 부서, 팀 中 한가지라도 선택하도록 제약
	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, Static7.value)) {
			var arr = [Static7.value];
			gfn_showMsg("CW00001", arr);
			return;
		}
	}

	// 필수조건이 입력되었다면 기간에 상관없이 조회 가능함.
	if(posid_yn == true || reqno_yn == true || jqprnum_yn == true) {

	} else {
		// 필수조건이 없을경우 조회조건을 1년으로 제한함
		// 수신결제기간 및 승인기간 그리고 업무구분지정기간이 모두 지정되지 않았을 경우에는 조회되지 않도록 처리

		if(length(trim(cal_redat_fm.Value)) <> 8 && length(trim(cal_redat_to.Value)) == 8) { // 수신결제기간의 시작일자가 없다면
			gfn_showAlert("수신결제기간의 시작일자를 확인해주세요.");
			return;
		}

		if(length(trim(cal_redat_fm.Value)) == 8 && length(trim(cal_redat_to.Value)) <> 8) { // 수신결제기간의 종료일자가 없다면
			gfn_showAlert("수신결제기간의 종료일자를 확인해주세요.");
			return;
		}

		if(length(trim(cal_apdat_fm.Value)) <> 8 && length(trim(cal_apdat_to.Value)) == 8) { // 승인기간의 시작일자가 없다면
			gfn_showAlert("승인기간의 시작일자를 확인해주세요.");
			return;
		}

		if(length(trim(cal_apdat_fm.Value)) == 8 && length(trim(cal_apdat_to.Value)) <> 8) { // 승인기간의 종료일자가 없다면
			gfn_showAlert("승인기간의 종료일자를 확인해주세요.");
			return;
		}

		if(length(trim(cal_apdat016_fm.Value)) <> 8 && length(trim(cal_apdat016_to.Value)) == 8) { // 업무구분지정기간의 시작일자가 없다면
			gfn_showAlert("업무구분지정기간의 시작일자를 확인해주세요.");
			return;
		}

		if(length(trim(cal_apdat016_fm.Value)) == 8 && length(trim(cal_apdat016_to.Value)) <> 8) { // 업무구분지정기간의 종료일자가 없다면
			gfn_showAlert("업무구분지정기간의 종료일자를 확인해주세요.");
			return;
		}

		if(
			((length(trim(cal_redat_fm.Value)) == 0 && length(trim(cal_redat_to.Value)) == 0)) &&
			((length(trim(cal_apdat_fm.Value)) == 0 && length(trim(cal_apdat_to.Value)) == 0)) &&
			((length(trim(cal_apdat016_fm.Value)) == 0 && length(trim(cal_apdat016_to.Value)) == 0))

		) { // 수신결제기간 및 승인기간 그리고 업무구분지정기간의 값이 모두 없다면 조회되지 않도록 처리
			gfn_showAlert("수신결제기간 또는 승인기간 또는 업무구분지정기간 중 한가지 검색조건을 입력해주세요.");
			return;
		}

		if(length(trim(cal_redat_fm.Value)) == 8 && length(trim(cal_redat_to.Value)) == 8) { // 수신결제기간이 값이 존재하며, 최대 1년으로 조회되도록 제한
			if(gfn_getDayCnt(ds_days1, cal_redat_fm.value, cal_redat_to.value) > 365) {
				gfn_showAlert("CW00078","12");
				cal_redat_fm.SetFocus();
				return;
			}
		}

		if(length(trim(cal_apdat_fm.Value)) == 8 && length(trim(cal_apdat_to.Value)) == 8) { // 승인기간이 값이 존재하며, 최대 1년으로 조회되도록 제한
			if(gfn_getDayCnt(ds_days2, cal_apdat_fm.value, cal_apdat_to.value) > 365) {
				gfn_showAlert("CW00078","12");
				cal_apdat_fm.SetFocus();
				return;			
			}		
		}

		if(length(trim(cal_apdat016_fm.Value)) == 8 && length(trim(cal_apdat016_to.Value)) == 8) { // 업무구분지정기간이 값이 존재하며, 최대 1년으로 조회되도록 제한
			if(gfn_getDayCnt(ds_days3, cal_apdat016_fm.value, cal_apdat016_to.value) > 365) {
				gfn_showAlert("CW00078","12");
				cal_apdat016_fm.SetFocus();
				return;				
			}		
		}

	}

	//보수콤보 적용 
	ufn_getGbn();

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs01:CS0102001H_S1");
	tit_addSearchActionInfo("cs01:CS0102001H_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_mm031=ds_mm031 ds_list=ds_list ds_exlist2=ds_exlist2"
        , ""
        , "fn_afterQuery"
        , true);

}

